INSERT INTO `rules` (`r_id`, `rule`, `content`, `level_id`) VALUES
(1, '(으)ㄹ 리가 있다 (없다)', '&lt;p&gt;бүх шалтгаан (учир) байхгүй&lt;/p&gt;', 0),
(2, '은/는 - ... бол', '<p>Нэр үг залгаж, тухайн нэр үгийн утгыг илүү тодотгож онцлоход хэрэглэгдэг туслах үг. Монгол хэлнээ яг дүйх утга байхгүй ч &quot;... бол&quot; гэсэн утгаар дүйцүүлж болно. 는 эгшгээр төгссөн нэр үгэнд, 은 гийгүүлэгчээр төгссөн нэр үгэнд тус тус залгана. Өгүүлэгдэхүүний үүргээр хэрэглэгдэх тохиолдолд уг нөхцөлийг хураангуйлж болно.</p>\r\n\r\n<p>저는 체첵입니다. Намайг Цэцэг гэдэг.</p>\r\n\r\n<p>제 이름은 체첵입니다. Миний нэрийг Цэцэг гэдэг..</p>\r\n', 0),
(3, '이/가', '<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Нэр үгэнд залгаж, өгүүлбэрийн эзэн биеийг илэрхийлдэг нэрлэхийн тийн ялгалын нөхцөл. Монгол хэлэнд нэрлэхийн тийн ялгалын нөхцөл тэг хэлбэрээр илэрсэн байдаг. Эгшгээр төгссөн нэр үгэнд <strong><span style=\"color:#FFA500\">가</span></strong>, гийгүүлэгчээр төгссөн нэр үгэнд <strong><span style=\"color:#FFA500\">이</span></strong> тус тус залгана. <strong><span style=\"color:#FFA500\">있습니다</span></strong> бол хүн, юмс үзэгдлийн оршин байгааг илэрхийлсэн <strong><span style=\"color:#FFA500\">있다</span></strong> (байх) тэмдэг үйл үгийн хүндэтгэлийн хэлбэр. Харин <strong><span style=\"color:#FFA500\">없습니다, 있습니다</span></strong> нь эсрэг утгатай үгс болно.</span></p>\r\n\r\n<p>책이 있습니다. Ном байгаа</p>\r\n\r\n<p>시계가 있습니다. Цаг байна</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>This particle is used with a noun to indicate that the noun is the subject of the sentence. When the noun ends in a consonant, use &#39;이&#39;. When it ends in a vowel, use &#39;가&#39;.</p>\r\n\r\n<p>이것이 책상입니다.</p>\r\n\r\n<p>여기가 학교입니다.</p>\r\n\r\n<p>이것이 책입니다.</p>\r\n\r\n<p>친구가 많습니다.</p>\r\n\r\n<p>의자가 없습니다.</p>\r\n\r\n<p>When it is used with the personal pronouns 나, 너, 저 and the interrogative pronoun 누구, it changes to &#39;내가&#39;, &#39;네가&#39;, 제가&#39;, or &#39;누가&#39;.</p>\r\n\r\n<p>내가 말했습니다.</p>\r\n\r\n<p>제가 김영수입니다.</p>\r\n\r\n<p>네가 가?</p>\r\n\r\n<p>누가 왔습니까?</p>\r\n', 0),
(4, '에 있습니다 - д, т байгаа', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Хүн,&nbsp;юмс үзэгдлийн орон байрыг заасан тийн ялгалын нөхцөл. Монгол хэлний өгөх оршихын тийн ялгалын нөхцөлтэй дүйнэ. Энэ нөхцөл <strong><span style=\"color:#FFA500\">있습니다, 없습니다</span></strong> үгтэй хамт орж, аливаа зүйлийн байгаа, байхгүй утгыг илэрхийлдэг.</span></p>\r\n\r\n<p>교실에 컴퓨터가 있습니까?&nbsp;Ангид компьютер байгаа юу?</p>\r\n\r\n<p>아니요, 없습니다. Үгүй, байхгүй.</p>\r\n', 0),
(5, '이/가 아닙니다 - биш', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><strong><span style=\"color:#FFA500\">아니다</span></strong> бол нэг үгтэй хамт хэрэглэгдэж, үгүйсгэх утга илэрхийлнэ. Монгол хэлнээ &quot;үгүй, биш&quot; гэсэн утгаар буудаг. <strong><span style=\"color:#FFA500\">아닙니다</span></strong> нь <span style=\"color:#FFA500\"><strong>아니다</strong></span> -ийн одоо цагийн хүүрнэх хэлбэр. Ингэхдээ нэр үгээ нэрлэхийн тийн ялгалаар хэлбэржүүлж <span style=\"color:#FFA500\"><strong>&quot;...&nbsp;이/가 아닙니다&quot;</strong></span> гэж хэрэглэдэгийг санаарай.</span></p>\r\n\r\n<p>Жнь:</p>\r\n\r\n<p>A) 이것은 사전입니까? Энэ толь бичиг үү?</p>\r\n\r\n<p>B) 아니요6 사전이 아닙니다. Үгүй, толь бичиг биш.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>It is used with a noun. It is the negation of &#39;이다&#39;. When the noun ends in a consonant, use &#39;이 아니다&#39;. When the noun ends in a vowel, use &#39;가 아니다&#39;.</p>\r\n\r\n<p>저는 일본 사람이 아닙니다.</p>\r\n\r\n<p>존슨 씨는 의사가 압니다.</p>\r\n\r\n<p>이것은 교과서가 아닙니다.</p>\r\n\r\n<p>린다 씨는 선생님이 이닙니다.</p>\r\n\r\n<p>우리 어머니는 한국 사람이 아닙니다.</p>\r\n', 0),
(6, '와/과 - ба, бөгөөд, болон', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Хоёр нэр үгийг холбож хэрэглэдэг. Монгол хэлнээ &quot;ба, бөгөөд, болон&quot; гэсэн холбох үгтэй утга дүйнэ. Эгшгээр төгссөн үгэнд <strong><span style=\"color:#FFA500\">와</span></strong>, гийгүүлэгчээр төгссөн үгэнд <strong><span style=\"color:#FFA500\">과</span></strong> залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">교실에 책상과 의자가 있습니다. Ангид ширээ болон сандал байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기에 시계와 가방이 있습니다. Энд цаг болон цүнх байна.</span></p>\r\n', 0),
(7, '에 - \"-д, -т\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Цаг хугацаа заасан нэр үгийн ард залгаж, ямар нэг үйлдэл болох үе, хугацааг илэрхийлнэ. Монгол хэлний өгөх оршихын тийн ялгалын &quot;-д, -т&quot; нөхцөлтэй утга дүйнэ. Харин 언제 (хэзээ), 어제 (уржигдар), 그저께 (өчигдөр), 오늘 (өнөөдөр), 내일 (маргааш), 모레 (нөгөөдөр) зэрэг цаг заасан дайвар үгстэй хамт хэрэглэгддэггүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">토요일에 영화를 봅니다. Хагас сайн өдөр кино үзнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">12월 10일에 생일 파티를 합니다. 12 сарын 10нд төрсөн өдрийн баяр тэмдэглэнэ.</span></p>\r\n', 0),
(8, '아/어요', '<p style=\"text-align: justify;\">Үйл үг болон тэмдэг үйл үгийн үндсэнд залгаж яригч этгээдийн бодол саналыг илэрхийлэхэд хэрэглэдэг нөхцөл. -ㅂ/습니다, -ㅂ/습니까 нь илүү хүндэтгэлийн, албархаг шинжтэй бол -아/어요 албаны бус, энгийн мэдрэмжийг төрүүлдэг. Асуух хэлбэрт дууны өнгийг өндөрсгөдөг.</p>\r\n\r\n<p>A) 지금 무엇을 해요? Одоо юу хийж байна?</p>\r\n\r\n<p>B) 학교에서 공부해요. Сургууль дээрээ хичээлээ хийж байна.</p>\r\n\r\n<p>Үйл үг, тэмдэг үйл үгийн үндэс эгшгээр төгссөн бол давхар орж байгаа эгшиг нь гээгдэж, бусад эгшиг нь хураангуйлагддаг. Жнь:</p>\r\n\r\n<p>가다 -&gt; 가아요 -&gt; 가요</p>\r\n\r\n<p>보다 -&gt; 보아요 -&gt; 봐요</p>\r\n\r\n<p>마시다 -&gt; 마시어요 -&gt; 마셔요</p>\r\n', 0),
(9, '에 가다', '<p><span style=\"font-size:14px\"><span style=\"color:#FFA500\"><strong>에 가다</strong></span> хэлбэрийн <span style=\"color:#FFA500\"><strong>에</strong></span> нь үйлийн өрнөх чиглэл, хүрэх газрыг илэрхийлэх бөгөөд <span style=\"color:#FFA500\"><strong>가다 / 오다 / 다니다</strong></span> (явах, ирэх, очих) зэрэг үйл үгтэй хамт хэрэглэдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">지금 학교에 가요. Одоо сургууль руугаа явж байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 한국대학교에 다녀요. Би Солонгос дээд суруугльд явдаг.</span></p>\r\n', 0),
(10, '안', '<p><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн өмнө орж үгүйсгэх утга илтгэдэг. &quot;Нэр үг + <span style=\"color:#FFA500\"><strong>하다</strong></span>&quot; хэлбэрээр үүсдэг үйл үгийн <strong><span style=\"color:#FFA500\">하다</span></strong> -ийн өмнө нь ордог.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">지금 학교에 안 가요. Одоо сургууль явахгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">동생이 사과를 안 먹어요. Дүү алим иддэггүй.</span></p>\r\n', 0),
(11, '았/었 - Өнгөрсөн цагийн нөхцөл', '<p style=\"text-align:justify\">Өгүүлбэрт гарч буй нөхцөл байдал, үйл явдал өнгөрсөн цагт болсныг илэрхийлэх үйлийн нөхцөл. Үйл үг болон тэмдэг үйлийн үндэс нь ㅏ, ㅗ эгшгээр төгссөн -았 - залгаж, ㅏ ㅗ -аас бусад эгшгээр юм уу 이다, 이니다 гэх үгсийн ард 었 залгана. Харин 하다 -ийн төрлийн үйл үг болон тэмдэг үйл үг 하였/했 болж хувирдаг. -았/었 - ийн дараа төгсгөх нөхцөл - 아/어요 орвол 았아요/었어요 буюу хоёуланд нь -어요 хэлбэрийг залгадаг.</p>\r\n\r\n<p>공원에 사람이 많았습니다. Парк дээр хүн их байсан шүү.</p>\r\n\r\n<p>저는 아침에 빵을 먹었어요. Би өглөө талх идсэн.</p>\r\n\r\n<p>Үйл үг, тэмдэг үйлийн үндэс эгшгээр төгсвөл ижил төрлийн эгшгийн нэгийг гээж, бусад эгшгийг хураангуйлж бичдэг.</p>\r\n\r\n<p>가다 -&gt; 가았아요 -&gt; 갔아요 - Гээсэн хэлбэр</p>\r\n\r\n<p>지난 주말에 여행을 갔아요. Өнгөрсөн долоо хоногийн сүүлээр аялалд явсан.</p>\r\n\r\n<p>보다 -&gt; 보았아요 -&gt; 봤어요 - Хураангуйлсан хэлбэр</p>\r\n\r\n<p>극장에서 영화를 봤아요. Кинк театрт кино үзлээ.</p>\r\n\r\n<p>마시다 -&gt; 마시었어요 -&gt; 마셨아요 - Хураангуйлсан хэлбэр</p>\r\n\r\n<p>카페에서 커피를 바셨어요. Кафед кофе уулаа.</p>\r\n', 0),
(12, '하고 - Хамтрахын тийн ялгалын нөхцөл', '<p>Нэр үгийн ард залгаж олон тооны зүйлийг тоочиж хэлэх, эсвэл тухайн үйлийг хамтран үйлдэгч болохыг илэрхийлсэн нөхцөл.</p>\r\n\r\n<p>저는 어제 가게에서 빵하고 우유를 샀습니다. Би өчигдөр дэлгүүрээс талх болон сүү авлаа.</p>\r\n\r\n<p>오늘하고 내일은 학교에 안 가요. Өнөөдөр, маргааш сургууль явахгүй.</p>\r\n\r\n<p>어제 친구하고 도서관에서 공부했어요. Өчигдөр найзтайгаа номын санд хичээлээ хийсэн.</p>\r\n', 0),
(13, '(으)ㅂ시다 - Шаардан хүсэх нөхцөл', '<p style=\"text-align: justify;\">Үйл үгэнд залгаж, нас бага эсвэл зиндаа ойр хүнд ямар нэг ажил хэрэг санал болгох юм уу, шаардах хүсэх үед энэ нөхцөлийг хэрэглэдэг. Тухайн үйл үгийн үндэс эгшгээр төгсвөл ㅂ시다, гийгүүлэгчээр төгсвөл 읍시다 хэлбэрийг залгана. Гэхдээ үйлийн үндэс ㄹ гийгүүлэгчээр төгссөн тохиолдолд зөвхөн ㅂ시다 залгадаг.</p>\r\n\r\n<p>우리 같이 영화를 봅시다. Хоёулаа хамт кино үзэцгээе.</p>\r\n\r\n<p>부산에 같이 갑시다. Бүсан хамт явцгаая.</p>\r\n\r\n<p>오늘은 같이 한국 음식을 먹읍시다. Өнөөдөр хамт солонгос хоол идэцгээе.</p>\r\n', 0),
(14, '(ㅡ)', '<p>&nbsp;</p>\r\n\r\n<p>(ㅡ) эгшгээр төгссөн үйл үгийн үндсэн дээр (жишээ нь: 크다, 바쁘다 гэх мэт) -아/어 - эгшгээр эхэлсэн дагавар залгавал ㅡ эгшиг гээгдэнэ. ㅡ эгшиг гээгдсэний дараа ㅡ эгшгийн өмнөх үе ㅏ/ㅗ эгшгээс бүтэсн бол -아요 нөхцөл залгана. Харин ㅏ/ㅗ -аас бусад эгшгээс бүтсэн бол -어요 нөхцөл залгана.&nbsp;</p>\r\n\r\n<p>아프다 : 아프 + -아요 -&gt; 아파요</p>\r\n\r\n<p>예쁘다 : 예쁘 + -어요 -&gt; 예뻐요</p>\r\n\r\n<p>배가 너무 고파요.</p>\r\n\r\n<p>민준 씨는 크가 커요.</p>\r\n\r\n<p>저는 어제 바빴어요.</p>\r\n', 0),
(15, '은/는 - бол, харин', '<p>Харьцуулсан утга оруулдаг туслах үг. Гийгүүлэгчээр төгссөн нэр үгэнд 은, эгшгээр төгссөн үгэнд 는 туслах үгийг залгана.</p>\r\n\r\n<p>라면은 쌉니다. 불고기는 비쌉니다.</p>\r\n\r\n<p>수박은 큽니다. 사과는 박습니다.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>This particle is used with a noun to denote the topic or theme of a sentence. When the noun ends in a consonant, use &#39;-은&#39;. When the noun ends in a vowel, use &#39;-는&#39;.</p>\r\n\r\n<p>서울은 한국의 수도입니다.</p>\r\n\r\n<p>저는 한국 사람입니다.</p>\r\n\r\n<p>It is also used to express comparison or emphasis.</p>\r\n\r\n<p>동생은 키가 큽니다. 저는 키가 작습니다.</p>\r\n\r\n<p>고기를 좋아합니다. 생선은 싫어합니다.</p>\r\n\r\n<p>It can be attached to most case particles, except for subject and object particles which drops in case when this topic particle is used.</p>\r\n\r\n<p>도서관에는 언제 갔습니까?</p>\r\n\r\n<p>이곳에서는 담배를 피울 수 없습니다.</p>\r\n\r\n<p>부모님에게만은 알리지 마세요.</p>\r\n', 0),
(16, '고 싶다 - маар4 байх, -ыг хүсэх', '<p style=\"text-align: justify;\">Үйл үгэнд залган&nbsp;тухайн үйл явдал биелээсэй хэлээн хүсч буй өгүүлэгч этгээдийн хүсэл эрмэлзлэлийг илтгэнэ. Үйлийн эзэн бие 3 дугаар бие дээр байх тохиолдолд 고 싶어 하다 хэрэглэнэ.</p>\r\n\r\n<p>저는 한국에 가고 싶어요. Би Солонгост очмоор байна.</p>\r\n\r\n<p>저는 컴퓨터를 사고 싶습니다. Би компьютер авмаар байна.</p>\r\n\r\n<p>바트 씨는 한국어를 배우고 싶어 해요. Бат солонгос хэл сурахыг хүсдэг.</p>\r\n', 0),
(17, '겠', '<p style=\"text-align: justify;\">Үйл үгийн үндэс болон хүндэтгэлийн -시 хэлбэр дээр залгаж хүсэл эрмэлзлэлийн утга илэрхийлнэ. Нэгдүгээр биеийн өгүүлэгчийн хүсэл эрмэлзлэлээс гадна асуух өгүүлбэрт хоёрдугаар биеийн сонсогчийн хүсэл эрмэлзлэлийг илэрхийлнэ.</p>\r\n\r\n<p>저는 콜라를 마시겠어요.</p>\r\n\r\n<p>저는 비빔밥을 먹겠습니다.</p>\r\n\r\n<p>바트 씨, 무엇을 드시겠어요.</p>\r\n', 0),
(18, '-지 않다', '<p>Үйл үг болон тэмдэг үйл үгийн үндсэнд залгаж, үгүйсгэх утга илтгэнэ.</p>\r\n\r\n<p>주말에는 바쁘지 않아요. Долоо хоногийн сүүлээр завтай байдаг.</p>\r\n\r\n<p>이 케이크는 달지 않습니다. Энэ торт нялуун биш.</p>\r\n\r\n<p>저는 김치를 먹지 않습니다. Би кимчи иддэггүй.</p>\r\n\r\n<p>우리 집은 넓지 않습니다.</p>\r\n\r\n<p>저는 키가 크지 않습니다.</p>\r\n\r\n<p>이 가방은 비싸지 않습니다.</p>\r\n\r\n<p>김치는 맵지 않아요.</p>\r\n\r\n<p>이 가방은 무겁지 않아요.</p>\r\n', 0),
(19, '(으)세요', '<p>Үйл үгийн үндсэнд залгаж, шаардан хүсэх утга илэрхийлнэ. -세요 эгшгээр төгссөн үйл үгийн үндсэнд, -으세요 гийгүүлэгчээр төгссөн үйл үгийн үндсэнд залгана.</p>\r\n\r\n<p>우유를 주세요. Сүү өгөөч.</p>\r\n\r\n<p>여기에 앉으세요. Энд суугаарай.</p>\r\n\r\n<p>8시에 학교에 오세요. 8 цагт сургууль дээр ирээрэй.</p>\r\n', 0),
(20, '(으)로', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Зүг чиг байрлал заасан нэр үгийн ард залгаж чиглэлийн утга заана. ㄹ-ээс бусад гийгүүлэгчээр төгссөн үгийн ард 으로, ㄹ-гийгүүлэгч болон эгшгээр төгссөн үгийн ард 로-г тус тус залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">위로 올라가세요. Дээшээ авираарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">왼쪽으로 오세요. Зүүн талаар нь ирээрэй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">교실로 드러가세요. Анги руу ороорой.</span></p>\r\n', 0),
(21, '-(으)ㄹ래요', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ярилцагч этгээдийн хүсэл санааг асуух, санал тавихад хэрэглэж ойр дотно харьцааг илэрхийлнэ. ㄹ -ээс бусад гийгүүлэгчээр төгссөн үйл үгийн үндсэнд -을래요, ㄹ гийгүүлэгч&nbsp;болон эгшгээр төгссөн үйл үгийн үндсэнд -ㄹ래요 -г тус тус залгадаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">바트 씨, 비빔밥을 먹을래요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">바트 씨, 우리 오늘 영화 볼래요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">등산 같이 갈래요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">커피 한 잔 하실래요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한강에서 배를 타지 않을래요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A: 저는 된장찌개를 먹을래요. 하미 씨는 뭐 드실래요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B: 저는 갈비탕을 먹을래요.</span></p>\r\n', 0),
(22, '도 - \"-ч, -ч гэсэн, -ч бас\"', '<p><span style=\"font-size:14px\">Нэр үгийн ард залгаж, монгол хэлнээ &quot;-ч, -ч гэсэн, -ч бас&quot; сул үгтэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">교실에 책상이 있습니다. 의자도 있습니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 김밥을 좋아해요. 불고기도 좋아해요.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">This particle is used with a noun to express additional information.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">책상이 있습니다. 의자도 있습니다. 시계도 있습니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">책이 많습니다. 그람도 많습니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">학교가 큽니다. 교실도 큽니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">농구를 하바니다. 야구도 합니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이름을 압니다. 주소도 압니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">To indicate a certain place or location, use with &#39;에&#39;, &#39;에서&#39;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">산에 삽니다. 바다에도 갑니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">도서관에서 공부합니다. 집에서도 공부합니다.</span></p>\r\n', 0),
(23, '수 관형사 (1) - Тоо заасан тэмдэг нэр (2)', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Хүн ба юмны тоо ширхэг заасан нэрийг тооны нэр гэнэ. Солонгос хэлний тооны нэрийг Солонгос болон ханзны тоо гэж ангилдаг. Тухайлбал, насаа тоолоход Солонгос тоог хэрэглэнэ. Тоо ширхэг заасан үгс голдуу тоологдох нэрийн өмнө орох бөгөөд энэ үед зарим тооны нэрийн үндэс хэлбэржиж хувирдаг. Тухайлбал, 스물 гэхийг 스물살, 스무개 гэх мэтээр хувиргаж хэрэглэнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">체첵 씨는 올해 스무살입니다. Цэцгээ энэ жил хорин настай</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 스물두 살이에요. Би хорин хоёр настай</span></p>\r\n\r\n<p><span style=\"font-size:14px\">우리 형은 서른네 살입니다. Манай ах гучин дөрвөн настай</span></p>\r\n', 0),
(24, '께서 - Хүндэтгэлийн тийн ялгал', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйлийн эзэн биеийг заадаг 이/가 нэрлэхийн тийн ялгалийн хүндэтгэлийн хэлбэр. 이/가 туслах нөхцөлтэй хамт орсон 께서는 нөхцөлийг үйлийн эзэн бие, субьектийг онцлох хүндэтгэх зорилгоор хэрэглэнэ. Монгол хэлэнд хүндэтгэх хэлбэрийн нэрлэхийн тийн ялгал <u>-тан, тэн, -бээр</u> хэлбэрээр илрэх ч орчин цагийн монгол хэлнээ өргөн хэрэглэгдэхээ больжээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">아바지께서 오십시다. Аав морилон айсуй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">선생님께서 전화하셨습니다. Багш(тан) утсаар ярьсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">할아버지께서는 텔레비전을 보십니다. Өвөө зурагт үзэж байна.</span></p>\r\n', 0),
(25, '(으)시 - Хүндэтгэлийн нөхцөл', '<p><span style=\"font-size:14px\">Өгүүлбэрийн субьектийн хүндэтгэх болбоос хөдөлгөөн заасан үйл үг юм уу тэмдэг үйл үгийн үндсэнд -(으)시 залгаж өгүүлэгдэхүүн гишүүний хүндэтгэсэн утга оруулна. Эгшгээр эсвэл ㄹ гийгүүлэгчээр төгссөн үйл үгийн үндсэнд -시 -г, ㄹ -ээс бусад гийгүүлэгчээр төгссөн үндсэнд -(으)시 -залгана. -아/어요 хэмээх төгсөх нөхцөл дээр -(으)시 -залгахдаа (으)세요 хэлбэрээр хураангуйлж бичнэ. Хүндэтгэлийн энэ нөхцөл монгол хэлэнд байдаггүй тул үйлийн үндсийг зөв хувиргах дасгал сайн хийж эзэмших шаардлагатай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">부모님께서는 올란바토르에 사십니다. Эцэг эх маань Улаанбаатарт амьдардаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">사장님, 오늘 신문을 읽으셨습니까? Дарга аа, өнөөдрийн сонин уншсан уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">우리 선생님은 한국분이세요. Манай багш Солонгос хүн шүү дээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Тодруулга 1</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Зарим үйл үгэнд -(으)시 - залгалгүйгээр тусгай хүндэтгэлийн үг хэрэглэх явдал бий. Энэ нь монгол хэлэнд ч илэрхдэг төстэй шинж юм.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">있다 -&gt; 계시다 байх</span></p>\r\n\r\n<p><span style=\"font-size:14px\">자다 -&gt; 주무시다 унтах (нойрсох)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">죽다 -&gt; 돌아가시다 үхэх (өөд болох)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Тодруулга 2</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Зарим нэр үг ч бас хүндэтгэлийн утга бүхий тусгай нэр үгтэй байдаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">집 -&gt; 댁 гэр (өргөө)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">생일 -&gt; 생신 төрсөн өдөр (мэндэлсэн өдөр)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">밥 -&gt; 진자 хоол (зоог)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">할아버지 생신이 언제예요? Өвөөгийн төрсөн өдөр хэзээ вэ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">아버님 성함이 어떻게 되세요? Аавын тань нэр алдар хэн билээ?</span></p>\r\n', 0),
(30, 'ㄹ - гийгүүлэгч гээгдэх ёс', '<p>ㄹ гийгүүлэгчээр төгссөн 살다, 알다, 만들다, 열다 гэх мэт үйл үгийн ард ㄴ, ㄹ, ㅂ, ㅅ зэрэг гийгүүлэгчээр эхэлсэн нөхцөл залгавал ㄹ гийгүүлэгч гээгдэх ёс бий.</p>\r\n\r\n<p>살다 : 사 + ㅂ니다 -&gt; 삽니다&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 열다 : 여 + 세요 -&gt; 여세요</p>\r\n\r\n<p>누나는 몽골에 삽니다. Эгч Монголд амьдардаг.</p>\r\n\r\n<p>창문을 여세요. Цонх онгойлгоорой.</p>\r\n\r\n<p>누가 김밥을 만드세요? Кимбаб хэн хийх вэ?</p>\r\n', 0),
(31, '-고 - ж, ч, н', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Хоёр болон түүнээс дээш үйл хөдлөл, нөхцөл байдлыг холбох үед хэрэглэнэ. Монгол хэлний үйлийн зэрэгцүүлэн холбох &#39;-ж, ч, н&#39; холбох нөхцөлтэй утгын хувьд дүйдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">비가 오고 바람이 불어요. Бороо орж салхи үлээнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">집이 크고 멋있어요. Том, гоё байшин байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">바트 씨는 몽골 사람이고 유진 씨는 한국 사람이에요. Бат Монгол, Юүжин бол Солонгос хүн.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Жич: Мөн цуварч болсон үйл хөдлөлийг холбоход энэ нөхцөлийг хэрэглэнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">손을 씻고 밥을 먹어요. Гараа угаагаад хоол иддэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">쇼핑을 하고 집에 갔어요. Дэлгүүрээр яваад гэртээ харив.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제 집에서 숙제하고 잤아요. Өчигдөр гэртээ даалгавраа хийгээд унтсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">수업이 끝나고 도서관에 가요. Хичээлээ тараад номын сан явдаг.</span></p>\r\n', 0),
(32, '부터 ~ 까지 - аас4 - хүртэл', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">부터 бол Монгол хэлний &quot;-аас<sup>4</sup>&quot; нөхцөлтэй дүйх бөгөөд аливаа үйл хөдлөл, цаг хугацааны эхлэлийг заана. Харин 까지 нөхцөл нь Монгол хэлний &quot;хүртэл&quot; гэсэн утгатай дүйх бөгөөд үйлийн төгсгөлийг заана. Энэ хоёр нөхцөлийг нэг өгүүлбэрт хамтад нь, эсвэл дан дангаар нь хэрэглэдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">세 시부터 다섯 시까지 운동을 합니다. Гурваас таван цаг хүртэл дасгал хийдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제는 밤 열두 시까지 공부했습니다. Өчигдөр шөнө 12 цаг хүртэл хичээлээ хийсэн.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">아침부터 지금까지 일했습니다. өглөөнөөс эхлээд одоо хүртэл ажил хийсэн.</span></p>\r\n', 0),
(33, '(으)ㄹ 거예요', '<p style=\"text-align: justify;\">Цаашид хийх ажил, түүнд хандах хувь хүний санаа зоригийг илтгэнэ. Монгол хэлний &quot;-даг хэрэг, -на<sup>4</sup>&quot; гэсэн нөхцөлтэй дүйнэ. Эгшгээр төгссөн үйл үгэнд -ㄹ 거예요, гийгүүлэгчээр төгссөн бол -을 거예요 хэлбэрийг залгана. Гэхдээ ㄹ гийгүүлэгчээр төгссөн үйл үгэнд -ㄹ 거예요 хэлбэрийг бас залгадаг. Мөн утга ижил ч хүндэтгэлийн зэрэглэлээр ялгарах -(으)ㄹ 겁니다 нөхцөлийг өргөн хэрэглэнэ.</p>\r\n\r\n<p>오늘 저녁에는 비빔밥을 먹을 거예요.</p>\r\n\r\n<p>다음 주에 한국에 갈 거예요.</p>\r\n\r\n<p>주말에 친구를 만날 거예요.</p>\r\n', 0),
(34, 'ㅂ - дүрмийн бус хувирал', '<p><span style=\"font-size:14px\">고맙다, 맵다, 줍다 гэх мэт ㅂ -аар төгссөн дүрмийн бус хувилалтай үйл үг, тэмдэг үйл үгийн үндсэн дээр эгшгээр эхэлсэн нөхцөл залгавал ㅂ гийгүүлэгч -우-ээр хувирна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">고맙다: 고바 + 우 + 어요 -&gt; 고마워요</span></p>\r\n\r\n<p><span style=\"font-size:14px\">주말 고마워요 - Үнэхээр их баярлалаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">김치는 매워요 - Кимчи халуун шүү.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제는 아주 추웠어요 - Өчигдөр их хүйтэн байсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Гэхдээ 돕다 гэдэг үйл үгэнд -아/어 - оор эхэлсэн нөхцөл залгавал ㅂ гийгүүлэгч&nbsp;오 -оор хувирна. Жишээ нь:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">돕다: 도 + 오 + 오요 -&gt; 도와요&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 도 + 우 + (으)ㄹ 거예요 -&gt; 도울 거예요</span></p>\r\n\r\n<p><span style=\"font-size:14px\">도와주세요. Тусламж өгөөч.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이 사람이 김 선생님을 도울 거예요. Энэ хүн Ким багшид тусална.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Жич: 입다, 좁다 гэх мэт үйл үгийг дүрмийн хувилалт үг гэнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">코트를 입어요. Пальто өмсдөг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">교실이 좁아요. Хичээлийн танхим жижиг байна.</span></p>\r\n', 0),
(35, '에게 / 한테 / 께', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Тухайн үйл хөдлөлийн байц гишүүнд хэрэглэдэг нарийн нөхцөл. Монгол хэлний өгөх оршихын &quot;-д, -т&quot; нөхцөлтэй дүйнэ. 한테 хэлбэрийг ярианы хэлэнд голдуу хэрэглэдэг бол 께 -г ахмад хүнийг хүндэтгэх үед хэрэглэнэ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>에게 - 말 &amp; 글,&nbsp;</strong></span><span style=\"font-size:16px\"><strong>한테 - 말,&nbsp;</strong></span><span style=\"font-size:16px\"><strong>께 - 높임말</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">민재 씨가 체첵 씨에게 선물을 주었습니다. Мин Жэ Цэцэгээд бэлэг өгсөн.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 현우 씨한테 전화했어요. Би Хён Ү - руу утасдсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">선생님께 전화를 드리세요. Багш руугаа утсаар яриарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 친구에게 전화해요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 친구한테 조화해요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 종생에게 선물을 줘요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 동생한테 선물을 줘요.</span></p>\r\n', 0),
(36, '지만 - вч, ч', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэд залган, хоёр ба түүнээс дээш өгүүлбэрийн утга харилцан эсрэгцэж буйг илтгэх үйлийн холбох нөхцөл. Монгол хэлний дутагдахын &quot;-вч, ч&quot; нөхцөлтэй нөхцлөтэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국어는 어렵지만 재미있어요. Солонгос хэл хэцүү боловч сонирхолтой юм.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">교실 밨은 춥지만 교실 안은 따뜻합니다. Тэнхимийн гадаа хүйтэн, харин тэнхим дотор дулаахан байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국 여행은 힘들었지만 즐거웠어요. Солонгосоор аялахад хэцүү байсан гэсэн ч хөгжилтэй байсан шүү.</span></p>\r\n', 0),
(37, '(으)려고 하다 - \"-х гэж байх, ... х санаатай\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж тухайн үйлийн санаа зорилгыг илтгэнэ. Монгол хэлний &quot;-х гэж байх, ...х санаатай&quot; гэсэн утгатай дүйнэ. Тухайн үйлийн үндэс эгшгээр төгссөн бол -려고 하다, гийгүүлэгчээр төгссөн бол -으려고 하다 хэлбэрийг залгана. Гэхдээ ㄹ гийгүүлэгчээр төгссөн үйлийн үндсэнд бас -려고 하다 хэлбэрийг залгаж болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 이번 겨울방학에 수영읗 배우려고 합니다. Энэ өвлийн амралтаар би усанд сэлж сурах гэж байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">주말에 가족과 함께 한국 음식을 먹으려고 합니다. Хагас бүтэн сайнаар гэрийхэнтэйгээ хамт Солонгос хоол идэх санаатай.</span></p>\r\n', 0),
(38, '-아/어서 - аад4', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгий үндсэнд залган, учир шалтгааны утга илтгэдэг холбох нөхцөл. Монгол хэлний &quot;-аад<sup>4</sup>&quot; холбох нөхцөлтэй утга дүйнэ. Үйлийн үндэс ㅏ, ㅗ эгшгээр төгссөн бол -아서; ㅏ, ㅗ эгшгээс бусад эгшгээр төгссөн бол -어서 хэлбэрийг залгана. Гэхдээ 하다 үйл үгийн хувьд 해서 хэлбэртэй болно. -아서/어서 нөхцөлийн өмнө өнгөрсөн цагийн нөхцөл ордоггүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">가방이 작아서 불편해요. Цүнх жижиг болохоор эвгүй юм.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">늦어서 죄송합니다. Хоцорсонд уучлаарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제는 피곤해서 일찍 잤습니다. Өчигдөр ядраад эрт унтсан.</span></p>\r\n\r\n<p>A. 어디에 가요? Хаашаа явж байгаа юм бэ?</p>\r\n\r\n<p>B. 부모님께서 몽골에서 오셔서 공항에 가요. Аав, ээж Монголоос ирээд онгоцны буудал явж байна.</p>\r\n\r\n<p>A. 어제 왜 학교에 안 왔어요? Өчигдөр яагаад сургуульдаа ирээгүй вэ?</p>\r\n\r\n<p>B. 배가 아파서 병원에 갔어요. Гэдэс өвдөөд эмнэлэг явсан.</p>\r\n\r\n<p>A. 왜 한국에 가요? Яагаад Солонгос руу явж байгаа вэ?</p>\r\n\r\n<p>B. 한국 친구를 만나고 싶어서 가요. Солонгос найзтайгаа уулзмаар санагдаад явж байна.</p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Тодруулга:&nbsp;</span></strong><span style=\"font-size:14px\">Учир шалтгаан заасан -아/어서 нөхцөлийн ард шаардан хүсэхийн -(으)세요, -(으)ㅂ시다 хэлбэр орохгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이 가방이 예뻐서 사세요. (x) Энэ цүнх хөөрхөн учраас аваарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">추워서 문을 닫읍시다. (x) Хүйтэн болохоор хаалгаа хаагаарай.</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Тодруулга:</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Жич: Нэр үгийн ард -(으)라서 хэлбэрийг залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">일요일이라서 백화점에 사람이 많습니다. Бүтэн сайн өдөр болохоор их дэлгүүрт хүн их байна.</span></p>\r\n', 0),
(39, '(으)ㄹ까요 - \"-х уу?, -х үү?\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залган, сонсогч этгээдээс санал асуух, санал тавих утга илтгэнэ. Монгол хэлнээ асуух хэлбэрийн &quot;-х уу<sup>4</sup>&quot; утгатай дүйнэ. Үйлийн үндэс эгшгээр төгсвөл -ㄹ까요, гийгүүлэгчээр төгсвөл -을까요 хэлбэрийг залгана. Мөн үйлийн үндэс ㄹ гийгүүлэгчээр төгссөн бол -ㄹ까요 хэлбэрийг залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A) (우리) 주말에 같이 영화를 볼까요? Бүгдээрэй хамт хагас бүтэн сайнаар кино үзэх үү?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B) 좋아요. 영화를 봅시다. Болж байна. Кино үзэцгээе.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A) (우리) 점심에 뭘 먹을까요? Бүгдээрэй өдрийн хоолонд юу идэх вэ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B) 불고기를 먹읍시다. Булгуги идэцгээе.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Тодруулга</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Жич: Эзэн бие гуравдугаар биед тохиолдолд, тодорхой бус зүйлийн талаар эргэлзэн асуух утга оруулна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A: 지금 김 선생님이 학교에 계실까요? Ким багш одоо сургууль дээр байгаа болов уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B: 아마 계실 거예요. Байгаа байлгүй дээ.</span></p>\r\n', 0),
(40, '못 - чадахгүй, эс чадах', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн өмнө орж тухайн үйлийг гүйцэлдүүлэх эзэн биеийн чадал хүрээгүй, эсвэл хүсэл байвч санаснаар болоогүй утга илтгэнэ.Монгол хэлний &quot;чадахгүй, эс чадах&quot; гэсэн утгатай дүйнэ. 명사 - 하다 хэлбэрийн 하다 -ийн өмнө 못 бичиж үгүйсгэх утга илтгэнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 김치를 못 먹어요. Би кимчи идэж чадахгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">바트 씨는 수영을 못 해요. Бат усанд сэлж чаддаггүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제 시간이 없어서 숙제를 못 했어요. Өчигдөр завгүй байсан болохоор даалгавраа хийж чадсангүй.</span></p>\r\n', 0),
(41, '(으)ㄹ 수 있다/없다 - \"-х чадах, -х чадахгүй\"', '<p style=\"text-align: justify;\">Үйл үг, тэмдэг үйлийн үндсэнд залган, тухайн үйлийн чадамжыг илтгэнэ. Үйлийн үндэс эгшгээр төгсвөл ㄹ 수 있다, гийгүүлэгчээр төгсвөл 을 수 있다 -г залгана. мөн үйлийн үндэс ㄹ гийгүүлэгчээр төгсвөл ㄹ 수 있다 -г залгадаг. Гэхдээ (으)ㄹ 수 없다 хэлбэр залгавал тухайн үйлийн чадамж муу болохыг илтгэнэ.</p>\r\n\r\n<p><span style=\"font-size:14px\">A) 토요일에 같이 등산할 수 있어요? Хагассайн өдөр хамт уул руу явж чадах уу?</span></p>\r\n\r\n<p>B) 네, 등산할 수 있어요. 토요일에는 시간이 있어요. Тийм. Уул руу явж чадна. Хагассайнд завтай.</p>\r\n\r\n<p>A) 수빈 씨, 오토바이를 탈 수 있어요? Сүбин аа, мотоцикл унаж чадах уу?</p>\r\n\r\n<p>B) 아니요, 저는 오토바이를 탈 수 없어요. Үгүй, Би мотоцикл унаж чадахгүй.</p>\r\n', 0),
(42, '에서 ~ 까지', '<p>에서 -&nbsp;аас<sup>4</sup> ~ 까지 -&nbsp;хүртэл</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Аливаа үйл хөдлөл, байдлын эхлэл болон хаанаас эхэлж хаа хүрснийг илтгэх нөхцөл. Монгол хэлний &quot;-аас<sup>4</sup>&nbsp;хүртэл&quot; гэсэн үг нөхцөлтэй дүйнэ.</strong></p>\r\n\r\n<p>Жнь:</p>\r\n\r\n<p>집에서 학교까지 어떻게 가요? Гэрээсээ сургууль хүртэл яаж явдаг вэ?</p>\r\n\r\n<p>A) 한국에서 몽골까지 시간이 얼마나 걸려요? Солонгосоос Монгол хүртэл хэдэн цаг зарцуулдаг вэ?</p>\r\n\r\n<p>B) 4 시간쯤 걸려요. 4 цаг орчим зарцуулдаг.</p>\r\n', 0),
(43, 'ㄷ', '<p>Дүрмийн бус ㄷ гийгүүлэгч</p>\r\n\r\n<p>ㄷ гийгүүлэгчээр төгссөн 걷다, 듣다, 묻다 гэх мэт үйл үгийн үндсэнд эгшгээр эхэлсэн дагавар залгавал ㄷ гийгүүлэгч ㄹ гийгүүлэгчээр солигдоно.</p>\r\n\r\n<p>듣다 : 듣 + 어요 -&gt; 들어요.</p>\r\n\r\n<p>무슨 노래를 들었어요? Ямар дуу сонссон бэ?</p>\r\n\r\n<p>그러면 선생님께 물어보세요. Тийм бол багшаасаа асуугаарай.</p>\r\n', 0),
(44, '(으)ㄴ 나머지', '<p>Үйл үг болон тэмдэг үйл үгийн үндсэнд залгаж, ямар нэгэн үйлийн буюу ямар нэгэн байдалд хүрсэн үр дүнг илэрхийлэхэд хэрэглэх бөгөөд тэгэхдээ тухайн үйл, байдал нь явцгүй болж, сөрөг үр дүн үүссэнийг илтгэнэ. Монгол хэлнээ &quot;-сан болохоор, -сэний уршгаар&quot; гэсэн утгатай дүйцнэ.</p>\r\n\r\n<p>자녀 양육의 문제로 출산을 기피한 나머지 출산율이 급격히 떨어졌다.</p>\r\n\r\n<p>핵가족화가 급속히 이루어진 나머지 가족에 대한 가치관이 붕괴되었다.</p>\r\n\r\n<p>그 영화배우는 상을 받고 너무 기쁜 나머지 사람들 앞에서 울음을 터뜨렸다.</p>\r\n', 0),
(45, '기 짝이 없다', '<p>Тэмдэг үйл үгийн үндсэнд залгаж, харьцуулахын аргагүй агуу байх, эсвэл хэрээс хэтэрсэнийг онцолж хэлэхэд хэрэглэдэг. Монгол хэлнээ &quot;хэтэрхий ... байх&quot;, &quot;нөхцөлгүй ... байх&quot;, &quot;-хээс4 өөр юм алга&quot; зэрэг хэллэгтэй утга дүйнэ.</p>\r\n\r\n<p>저출산 고령화 문제에 대한 정부의 정책은 실망스럽기 짝이 없다.</p>\r\n', 0),
(46, '(으)로 - үйлдэхийн \"-аар4\" тийн ялгал', '<p>Аливаа үйл хөдлөл ямар хэрэгсэл, арга замаар бүтсэнийг илтгэх нөхцөл. Монгол хэлний үйлдэхийн &quot;-аар<sup>4</sup>&quot; нөхцөлтэй дүйнэ. Гийгүүлэгчээр төгссөн нэр үгэнд 으로, эгшгээр төгссөн нэг үгэнд 로 хэлбэрийг хэрэглэнэ. Гэхдээ ㄹ гийгүүлэгчээр төгссөн нэр үгийн ард мөн ㄹ нөхцөл орно.</p>\r\n\r\n<p>Жнь:</p>\r\n\r\n<p>저는 학교까지 버스로 와요. Би сургуульдаа автобусаар очдог.</p>\r\n\r\n<p>한국 사람은 버스와, 지하철로 회사에 가요. Солонгосчууд ихэвчлэн автобус, метрогоор ажилдаа явдаг.</p>\r\n\r\n<p style=\"text-align:center\"><strong>Тодруулга</strong></p>\r\n\r\n<p>Ямар нэг тээврийн хэрэгсэл ашигласныг илтгэх &#39;нэр үг + (으)로&#39; хэлбэрийн дараа 가다/오다 гэх мэтийн үйл үг ихэвчлэн ордог. Мөн тухайн нэг тээврийн хэрэгслийн ард 타다 (суух) гэдэг үг орвол &#39;нэр үг + 을/를 타다&#39; гэсэн хэлбэртэй болно.</p>\r\n\r\n<p>버스로 가다/버스를 타다 автобусаар явах, автобусанд суух.</p>\r\n', 0),
(48, '(으)러 가다 - хаар4 явах', '<p>Үйл үг залган тухайн үйлдлийн зорилго юу болохыг заана. 가다 үйл үгийн оронд 오다, 나가다, 나오다, 들어가다 гэх мэт үгсийг орлуулан хэрэглэж болно. Гийгүүлэгчээр төгссөн үйл үгийн үндсэнд -으러, эгшгээр төгссөн үндсэнд -러 нөхцөл залгадаг. Гэхдээ ㄹ гийгүүлэгчээр төгссөн үйл үгийн үндсэнд ч мөн -러 нөхцөл залгана.</p>\r\n\r\n<p>저는 한국에 한국어 공부하러 왔어요. Би солонгос хэл сурахаар Солонгост ирсэн.</p>\r\n\r\n<p>우리는 지금 밥 먹으러 가요. Бид одоо хоол идэхээр явлаа.</p>\r\n\r\n<p>토요일에 공원에 놀러 갈까요? Хагас сайнд паркд тоглохоор явцгаах уу?</p>\r\n', 0),
(49, '입니다', '<p>Нэр үгийн ард 이다 нөхцөлийг залгаж, нэр өгүүлэхүүний утга илтгэдэг. 입니다 нь 이다 -ийн одоо цагийн хүүрнэх хэлбэр бөгөөд илүү эелдэг утгатай. Монгол хэлнээ нэр өгүүлэхүүн нь &quot;-байх, -болох, -юм&quot; зэрэг хэлбэрээр илрэх боловч заримдаа дээрх туслах үг хураангуйлагддагийг солонгос хэлтэй хольж 이다 нөхцөлийг хэрэглэхгүй орхидгийг анхаарна уу.</p>\r\n\r\n<p>바야르입니다.</p>\r\n\r\n<p>학생입니다.</p>\r\n\r\n<p>몽골 사람입니다.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&#39;이다&#39; is attached to the end of a noun to make the noun function like a verb. It is used to express the identity or state of a subject, or to designeate an object. &#39;입니다&#39; is used to politely speak to the listener.</p>\r\n\r\n<p>이것은 제 첵상입니다.</p>\r\n\r\n<p>저는 학생입니다.</p>\r\n\r\n<p>이 아이가 제 딸입니다.</p>\r\n\r\n<p>여기가 우리 집입니다.</p>\r\n\r\n<p>그분은 의사입니다.</p>\r\n', 0),
(50, '기/는 것', '<p>-기/-는 것 -х, х нь</p>\r\n\r\n<p>Үйл үг, тэмдэг үйлийн үндсэнд залган өгүүлбэрт өгүүлбэгдэхүүн, тусагдахуун гишүүний үүрэг гүйцэтгэх нэр үг үүсгэнэ. -기, -는 것 хэлбэрийн ард 이/가, 을/를 гэх мэт тийн ялгахын нөхцөл залгаж боло. энэ хэлбэр монгол хэлний &quot;-х, -х нь&quot; хэлбэртэй дүйх бөгөөд өгүүлэгдэхүүн гишүүн өгүүлбэр үүсгэдэг байдал нь хоёр хэлнээ ижил аж.</p>\r\n\r\n<p>제 취미는 영화 보기예요. Би кино үзэх хоббитой.</p>\r\n\r\n<p>저는 혼자 걷는 것을 좋아해요. Би ганцаараа алхах дуртай.</p>\r\n', 0),
(51, '입니까?', '<p>Нэр өгүүлэхүүн нөхцөлийн асуух хэлбэр</p>\r\n\r\n<p>입니다 -ийн асуух хэлбэр. Нэр үгэнд залгаж, тухайн нэр үгийн талаар лавлан асуухад хэрэглэнэ.</p>\r\n\r\n<p>체첵 씨입니까?</p>\r\n\r\n<p>학생입니까?</p>\r\n\r\n<p>몽골 사람입니까?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>It is the interrogative form of &#39;입니다&#39;. A question mark is used at the end.</p>\r\n\r\n<p>이분이 어느 나라 사람입니까?</p>\r\n\r\n<p>지금이 두 시입니까?</p>\r\n\r\n<p>여기가 신촌입니까?</p>\r\n\r\n<p>이 책은 누구 책입니까?</p>\r\n\r\n<p>오늘이 며칠입니까?</p>\r\n', 0),
(52, 'ㅂ/습니다', '<p>Үйл үгийн одоо цагийн нөхцөлийн хүүрнэр хэлбэр</p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж, сонсогч этгээдийг хүндэтгэж бодол санаагаа илэрхийлэхэд хэрэглэгдэнэ. Монгол хэлнээ &quot;-ж байна, -даг&quot; нөхцөлтэй утга дүйнэ. Эгшгээр төгссөн үгийн үндсэнд <strong><span style=\"color:#FFA500\">-ㅂ니다</span></strong>, гийгүүлэгчээр төгссөн үгийн үндсэнд <strong><span style=\"color:#FFA500\">-습니다</span></strong> залгана.</span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p>This final ending is used with a verb stem to formally explain or declare something to a listener. When the verb stem ends in a vowel, use &#39;-ㅂ니다&#39;. When the verb stem ends in a consonant, use &#39;-습니다&#39;.</p>\r\n\r\n<p>학교에 갑니다.</p>\r\n\r\n<p>저는 키가 큽니다.</p>\r\n\r\n<p>만나서 반갑습니다.</p>\r\n\r\n<p>책이 있습니다.</p>\r\n\r\n<p>의자가 없습니다.</p>\r\n', 0),
(53, 'ㅂ니까/습니까', '<p><span style=\"font-size:14px\">Үйл үгийн одоо цагийн нөхцөлийн асуух хэлбэр</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><span style=\"color:#FFA500\">-ㅂ/습니다</span></strong> -ийн асуух хэлбэр. Эгшгээр төгссөн үгийн үндсэнд <span style=\"color:#FFA500\"><strong>-ㅂ니까</strong></span>, гийгүүлэгчээр төгссөн үгийн үндсэнд <span style=\"color:#FFA500\"><strong>-습니까</strong></span> залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 지원 씨가 일합니까? Живон ажиллаж байгаа юу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 지원 씨가 일합니다. Тийм ээ, Живон ажиллаж байгаа.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>This final ending is used with a verb stem to formally ask a question. When the verb stem ends in a vowel, use &#39;-ㅂ니까?&#39;. When the verb stem ends in a consonant, use &#39;-습니까?&#39;.</p>\r\n\r\n<p>무엇을 합니까?</p>\r\n\r\n<p>어디에 갑니까?</p>\r\n\r\n<p>사전이 있습니까?</p>\r\n\r\n<p>한국 친국가 없습니까?</p>\r\n\r\n<p>시간이 많습니까?</p>\r\n', 0),
(54, '이/그/저', '<ul>\r\n	<li>이</li>\r\n</ul>\r\n\r\n<p>This adnominal form is used in front of a noun to refer to something close to the speaker.</p>\r\n\r\n<p>이 책이 사전입니까?</p>\r\n\r\n<p>이 연필이 제 언필입니다.</p>\r\n\r\n<p>It is also used to refer to the fact just mentioned.</p>\r\n\r\n<p>이 이야기를 누가 압니까?</p>\r\n\r\n<p>이 사실을 이야기하지 마세요.</p>\r\n\r\n<ul>\r\n	<li>그</li>\r\n</ul>\r\n\r\n<p>This adnominal form is used in front of a noun to refer to something close to the listener</p>\r\n\r\n<p>그 책은 교과서입니다.</p>\r\n\r\n<p>그 연플을 나에게 주십시요.</p>\r\n\r\n<p>It can be also used to refer to something that the speaker and the listener both knoww or the fact mentioned before.</p>\r\n\r\n<p>아까 그 남자가 누구입니까?</p>\r\n\r\n<p>요즘 그 영화를 안 본 사람이 없어요.</p>\r\n\r\n<ul>\r\n	<li>저</li>\r\n</ul>\r\n\r\n<p>It is used with a noun to refer to something that is far from both the speaker and the listener.</p>\r\n\r\n<p>나는 저 사람을 압니다.</p>\r\n\r\n<p>저 건물이 우리 학교입니다.</p>\r\n', 0),
(55, '밖에', '<p>Нэр үг, дайвар үгийн ард залгаж, сонголтын өөр боломж нөхцөл байхгүй, зөвхөн үүнийг л сонгохоос өөр аргагүй гэсэн утгыг илэрхийлж, монгол хэлнээ &quot;-аас<sup>4</sup>&nbsp;өөр&quot; гэсэн утгатай дүйнэ. 만 нь хүүрнэх болон үгүйсгэх өгүүлбэрт хоёуланд нь хэрэглэгддэг бол 밖에 нь голдуу үгүйсгэх өгүүлбэрт ордог.</p>\r\n\r\n<p>시험 시간이 5분밖에 안 남았어요.</p>\r\n\r\n<p>보고서를 제출한 사람이 반밖에 안 돼요.</p>\r\n\r\n<p>이번 시험에서 한 개밖에 안 틀렸어요.</p>\r\n\r\n<p>A. 볼펜 좀 빌려 주세요.</p>\r\n\r\n<p>B. 저도 볼펜이 하나밖에 없어요. 미안해요.</p>\r\n\r\n<p>A.왜 그렇게 안 먹어요?</p>\r\n\r\n<p>B.긴장이 돼서 조금밖에 못 벅겠어요.</p>\r\n', 0),
(56, '(이)라고 하다', '<p>Нэр үгийн ард залгаж, өгүүлэгч нөгөө хүндээ өөрийгөө болон бусдыг танилцуулахад, мөн эд зүйлсийг нэрлэхэд хэрэглэнэ. Монгол хэлнээ &quot;... гэдэг&quot; гэсэн утгатай дүйнэ. Эгшгийн ард 라고, гийгүүлэгчийн ард 이라고 -г тус тус залгана.</p>\r\n\r\n<p>처음 뵙곗습니다. 저는 체첵이라고 합니다.</p>\r\n\r\n<p>이 사람은 제 친구 조민재라고 해요.</p>\r\n\r\n<p>&#39;전공&#39;을 몽골 말로 &#39;Мэргэшил&#39; (이)라고 해요.</p>\r\n', 0),
(57, '-게 되다', '<p>Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж, өгүүлбэрийн эзэн биеийн хүсэл эрмэлзэлээс хамаарахгүй гадны хүчин зүйлсээс болж ямар нэгэн үйлдэл хийх болсон, эсвэл тухай нөхцөл байдал өөрөөр эргэсэнийг илэрхийлнэ. Монгол хэлнээ &quot;-х болох, -хээр<sup>4</sup> болох&quot; гэсэн утгаар хэрэглэгддэг.</p>\r\n\r\n<p>이번 학기에는 장학금을 받게 됐어요.</p>\r\n\r\n<p>컴퓨터 게임을 시작하면 계속하게 돼요.</p>\r\n\r\n<p>건강이 나빠져서 운동을 하게 됐어요.</p>\r\n', 0),
(58, '(으)ㄹ 생각이다', '<p>Үйл үгийн үндсэнд залгаж санаа бодол, зорилгыг илэрхийлэх бөгөөд монгол хэлнээ &quot;-х бодолтой байх&quot; гэсэн утгатай дүйнэ. Үйл үгийн үндэс ㄹ гийгүүлэгч болон эгшгээр төгссөн бол -ㄹ 생각이다, ㄹ-ээс бусад гийгүүлэгчээр төгссөн бол -을 생각이다 -г тус тус залгана.</p>\r\n\r\n<p>책을 찾으러 도서관에 갈 생각이에요.</p>\r\n\r\n<p>수업 후에 점심을 먹을 생각이에요.</p>\r\n\r\n<p>저는 대학원에 진학할 생각인데 조언 좀 해 주세요.</p>\r\n', 0),
(59, '는 길이다', '<p>Голдуу 가다, 오다 зэрэг үйл үгийн үндсэнд залгаж, тухайн үйлдэл өрнөж буй утга илтгэнэ. Монгол хэлнээ &quot;... явж байна, ирж байна&quot; гэсэн утгатай дүйнэ. 가다, 오다 орсон нийлмэл үг 나가다, 나오다, 들어가다, 들어오다, 내려가다, 내려오다, 올라가다, 올라오다 болон 출근하다 (ажилдаа явах), 퇴근하다 (ажлаасаа тарах) зэрэг үйл үгэнд ч хэрэглэдэг. -는 길에 орох тохиолдолд явах (ирэх) замдаа, явах (ирэх) далимдаа гэсэн утгатай.</p>\r\n\r\n<p>지금 모임에 가는 길이에요.</p>\r\n\r\n<p>친구를 만나러 시내에 가는 길이에요.</p>\r\n\r\n<p>집에 돌아오는 길에 사점에 가서 책을 샀어요.</p>\r\n', 0),
(60, '(으)ㄴ 덕분에', '<p>Үйл үгийн үндсэнд залгаж, өмнөх өгүүлбэрийн үйлдэл дараагийн үйлдлийнхээ үндэс, шалтгаан болж байгааг илрэхийлэх бөгөөд монгол хэлнээ &quot;-сэний ачаар, -сэний хүчинд&quot; гэсэн утгатай дүйнэ. ㄹ гийгүүлэгч болон эгшгээр төгссөн үйл үгийн үндсэнд -ㄴ 덕분에, ㄹ -ээс бусад гийгүүлэгчээр төгссөн үйл үгийн үндсэнд -은 덕분에 -г тус тус залгана.</p>\r\n\r\n<p>체첵 씨가 도와주 덕분에 일이 잘 끝났습니다.</p>\r\n\r\n<p>선생님께서 잘 가르쳐 주신 덕분에 한국어 실력이 좋아졌습니다.</p>\r\n\r\n<p>선생님 덕분에 한국 생활을 무사히 마칠 수 있었습니다.</p>\r\n', 0),
(61, '여기/거기/저기 - энд, наана чинь, тэнд', '<p>여기 (энд) нь яригч этгээдийн байрлал заасан төлөөний үг.&nbsp;거기 (наана чинь) нь яригч этгээдэд хол, сонсогчид ойрхон газрыг заасан төлөөний үг.&nbsp;저기 (тэнд) гэдэг нь яригч, сонсогч этгээдэд хоёуланд нь хол байгаа байрлалыг заасан төлөөний үг.</p>\r\n\r\n<p>여기/거기/저기는 도서관입니다. Энэ/ наад/ тэр (газар) бол номын сан.</p>\r\n\r\n<p>여기/거기/저기는 은행입니다. Энэ/ наад/ тэр (газар) бол банк.</p>\r\n', 0),
(62, '이것/그것/저것 - энэ, наадхи, тэр юм', '<p>이것 (энэ юм) нь яригчийн байгаа эд юмсыг заасан төлөөний үг.&nbsp;거것 (наадхи юм) нь ярьж байгаа хүнд хол, сонсож байгаа хүнд ойрхон нд юмсыг заасан төлөөний үг.&nbsp;저것 (тэр юм)&nbsp; нь яригч, сонсогчид хоёуланд нь хол байгаа эд юмсыг хэлэхэд хэрэглэдэг төлөөний үг.</p>\r\n\r\n<p>이것/그것/저것은 책입니다. Энэ/ наад/ тэр (юм) бол ном.</p>\r\n\r\n<p>이것/그것/저것은 사전입니다. Энэ/ наад/ тэр (юм) бол толь бичиг.</p>\r\n', 0),
(63, '을/를 - \"-ийг, -ыг, -г\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгэн дээр залгаж, тусагдахуун гишүүний үүргээр хэрэглэгддэг тийн ялгалын нөхцөл. Монгол хэлний заахын тийн ялгалын &quot;-ийг, -ыг, -г&quot; нөхцөлтэй дүйнэ. Эгшгээр төгссөн үгэнд <span style=\"color:#FFA500\"><strong>를</strong></span>, гийгүүлэгчээр төгссөн үгэнд <span style=\"color:#FFA500\"><strong>을</strong></span> залгана.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지원 씨가 빵을 먹습니다. Живон талхыг идаж байна.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">학생이 책을 읽습니다. Оюутан номыг уншиж байна.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">준영 씨가 친구를 만납니다. Жүнён найзтайгаа уулзаж байна.</span></p>\r\n', 0),
(64, '에서', '<p><span style=\"font-size:14px\">Өгөх оршихын тийн ялгалын нөхцөл</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгэнд залгаж, үйл явдал тухайн орон байранд өрнөх утга илэрхийлнэ. Энэ нөхцөл гарахын тийн ялгалын утга илэрхийлдэг ч дээрхи тохиолдолд монгол хэлний өгөх оршихын тийн ялгалын &quot;-д, -т&quot; нөхцөлтэй дүйнэ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">체첵 씨가 집에서 쉽니다. Цэцэг гэртээ амарч байгаа.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">수빈 씨가 은행에서 일합니다. Сүбин банкинд ажилладаг.</span></p>\r\n', 0),
(65, '한자어 수사 - Ханзны тоо', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Аливаа юм үзэгдлийн тоо хэмжээг ханзны тоо, солонгос тоо гэсэн хоёр янзын тоогоор илэрхийлдэг. Тоо хэмжээг заасан тооны нэр нь голдуу тооллого үгийн өмнө ордог учраас голуу тооллого үгийн төрлөөс хамаарч тоог өөр өөрөөр хэрэглэдэг. Жил, сар, өдрийн огноо, утасны дугаар, давхар дугаар, давхар зэргийг илэрхийлэхэд ханзны тоо хэрэглэдэг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">오늘은 2월 15일합니다. Өнөөдөр 2 сарын 15.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">제 전화번호는 139-287-3195 입니다. Миний утасны дугаар 9199-3195.</span></p>\r\n', 0),
(66, '고유어 수사 - Солонгос тоо', '<p>Солонгос хэлэнд солонгос тоо, ханзны тоо гэсэн хоёр янзын тоо хэрэглэгддэг. Солонгос тоогоор тоолж суръя.</p>\r\n\r\n<p>100 аас дээш тоонд ханзны тоог хэрэглэдэг.</p>\r\n', 0),
(67, '의', '<p>Нэр үгэнд залгаж, өмнөх үг нь дараагийн үгтэйгээ хамааралгүй болохыг илэрхийлдэг нөхцөл. Монгол хэлнээ харъяалахын тийн ялгалын &quot;-ын, -ийн, -н&quot; нөхцөлтэй дүйнэ. Ихэнх тохиолдолд 의 нөхцөл хураангуйлагдаж хэрэглэгддэгүй.</p>\r\n\r\n<p>오늘은 친구의 생일입니다. Өнөөдөр найзын төрсөн өдөр.</p>\r\n\r\n<p>김 선생님의 가방이 책상 위에 있어요. Ким багшийн цүнх ширээн дээр байна.</p>\r\n\r\n<p>토요일에 바트 씨의 친구를 만나려고 해요. Хагас сайн өдөр Батын найзтай уулзах гэж байна.</p>\r\n\r\n<p>아버지의 동생은 작은아버지입니다. Аавын дүүг авга ах гэдэг.</p>\r\n\r\n<p>저 책이 체첵 씨의 것입니다. Тэр ном Цэцэгийн ном.</p>\r\n\r\n<p>- &lt;Тайлбар&gt; Миний гэсэн утга бүхий 저의, 나의 үгс хураангуйлагдаж 제, 내 хэлбэртэй байна.</p>\r\n\r\n<p>제 이름은 빌궁입니다. Миний нэрийг Билүүн.</p>\r\n\r\n<p>그 사람은 내 동생이에요. Тэр хүн миний дүү.</p>\r\n\r\n<p>제 가족을 소개하겠습니다. Би гэр бүлээ танилцуулъя.</p>\r\n\r\n<p>이것이 내 전화번호예요. Энэ миний утасны дугаар.</p>\r\n\r\n<p>내 친구의 이름은 유나예요. Найзын маань нэр Юүна.</p>\r\n', 0),
(68, '-(으)ㄹ 때', '<p>Тэмдэг үйл үг болон үйл үгийн үндсэнд залгаж ямар нэгэн үйл болж байгаа эсвэл үргэлжилж буй цаг хугацааг илэхийлдэг. Монгол хэлнээ &quot;-х үед, -х дээ&quot; зэрэг утгаар буудаг. Үйл үг, тэмдэг үйл үгийн үндэс нь гийгүүлэгчээр төгссөн бол -을 때, эгшгээр төгссөн бол -ㄹ 때, ㄹ гийгүүлэгчээр төгссөн бол ㄹ гийгүүлэгчээр гэж -ㄹ 때-г тус тус залгана.</p>\r\n\r\n<p>아플 때 이 약을 드세요. Өвдөх үед энэ эмийг уугаарай.</p>\r\n\r\n<p>밥을 먹을 때 전화가 왔어요. Хоол идэх үед утастсан.</p>\r\n\r\n<p>A. 언제 태권도를 배웠어요? Хэзээ тэквондо сурсан бэ?</p>\r\n\r\n<p>B. 한국에서 살 때 태권도를 배웠어요. Солонгосд амьдарч байхдаа тэквондо сурсан.</p>\r\n\r\n<p>이 옷을 만들 때 정말 힘들었어요. Энэ хувцсыг хийхэд үнэхээр хэцүү байсан.</p>\r\n\r\n<p>한국말을 들을 때 좀 어려워요. Солонгос хэлийг сонсоход жаахан хэцүү.</p>\r\n\r\n<p>날씨가 더울 때 수영장에 가요. Цаг агаар халуун үед бассейн явдаг.</p>\r\n', 0),
(69, '-아/어 주다 - ', '<p><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, хэн нэгэнд ямар нэг зүйлийг хийж өгөх утга илтгэнэ. Монгол хэлнээ &quot;-ж өгөх&quot; гэсэн утгатай дүйнэ. Үйл үгийн үндсийн сүүлийн үенд ㅏ, ㅗ эгшиг орсон байвал -아 주다, ㅏ, ㅗ -аас бусад эгшиг орсон тохиолдолд -어 주다 -г тус тус залгана. 하다 -гаар төгссөн үг 해 주다 хэлбэртэй болдог.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">창문을 좀 닫아 주세요. Цонх хаагаад өгөөч.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">나는 친구의 가방을 들어 줍니다. Би найзынхаа цүнхийг барьж өгдөг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">초대해 주셔서 감사합니다. Урьсанд баярлалаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이것 좀 도와주세요. Та туслаад өгөөч.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 제가 사진을 찍어 드릴까요? Би зураг дараах өгөх үү?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 부탁합니다. Тэгээч, гуйж байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">지우개 좀 빌려 주시겠어요? Баллуураа түр өгөхгүй юу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- &lt;Тайлбар&gt; Тухайн үйлийн тусагдахуун нь өгүүлэгч этгээдээс нас, албан тушаалаар өндөр байвал -아/어 주다 -ийн оронд -아/어 드라다 хэлбэрийг хэрэглэдэг.</span></p>\r\n', 0),
(70, '만', '<p><span style=\"font-size:14px\">Альваа зүйлсээс нэг зүйлийг онцолж хэлэхэд хэрэглэх ба монгол хэлнээ гэсэн үгтэй утга дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">우리 반 학생은 모두 12면이에요. 저만 남자입니다. Манай анги 12 оюутнатай. Зөвхөн би л эрэгтэй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">아침에는 밥을 먹지 않습니다. 커피만 마십니다. Өглөө хоол иддэггүй. Зөвхөн кофе л уудаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국어 수업 시간에는 한국말로만 이야기합시다. Солонгос хэлний хичээл дээр зөвхөн солонгосоор л ярьцгаая.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 과일을 다 좋아하지만 수박만 싫어합니다. Би бүх жимсд дуртай ч тарвасанд л дургүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 토요일에도 학교에 갑니까? Хагас сайнд ч сургууль явдаг уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아니요, 평일에만 갑니다. Үгүй. Ажлын өдрүүдэд л явдаг.</span></p>\r\n', 0),
(71, '-(으)니까', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Эхний өгүүлбэр нь дараагийн өгүүлбэрийн учир шалтгаан, дүгнэлтийн үндэс болж байгааг илэрхийлдэг холбох нөхцөл. Монгол хэлний тул, учраас, болохоор гэсэн холбоос үгтэй ижил утгатай. Үйл үг, тэмдэг үйл үгийн үндэс эгшиг бол ㄹ гийгүүлэгчээр төгсвөл -니까, ㄹ гийгүүлэгчээс бусад гийгүүлэгчээр төгсвөл -으니까 -г тус тус залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">오늘은 약속이 있으니까 내일 만납시다. Өнөөдөр өөр болзоотой тул маргааш уулзъя.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">날씨가 추우니까 옷을 많이 입으세요. Цаг агаар хүйтэн учир хувцсаа сайн өмсөөрэй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 지금 이야기 좀 할 수 있어요? Одоо ярих боломжтой байна уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 미안해요. 지금 바쁘니까 이따가 이야기합시다. Уучлаарай, Одоо завгүй болохоор дараа ярилцъя.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">비가 오니까 우산을 가져가세요. Бороо орж байна, шүхэрээ авч яваарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">배가 고프니까 지금 밥을 먹을까요? Гэдэс өлсөн байна, одоо хоол идэх үү?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">길이 막히니까 지하철을 탑시다. Зам бөглөрөөд байна, метрогоор явъя.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- &lt;Тайлбар&gt; Нэр үгэнд залгах тохиолдолд эгшгээр төгсвөл -니까, гийгүүлэгчээр төгсвөл -이니까 залгана.</span></p>\r\n', 0),
(72, '-지 말다 - \"битгий -ээрэй4\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Сонсогч этгээдэд ямар нэгэн үйлдэл хийхийг хориглох нөхцөл. Монгол хэлний &quot;битгий -ээрэй4&quot; гэсэн утгатай дүйнэ. Захирах өгүүлбэрт &quot;битгий -ээрэй4&quot; гэсэн утга бүхий -지 마세요, -지 마십시요 хэлбэрийг, уриалан хүсэх өгүүлбэрт &quot;-хээ4 больцгооё&quot; гэсэн утга бүхий -지 맙시다 хэлбэрийг хэрэглэдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">버스를 타지 마세요. Автобусанд битгий суугаарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">사진을 찍지 마십시오. Зураг битгий дараарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">쓰레기를 버리지 맙시다. Хог хаяцгаая больцгооё.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기서 음식을 먹지 마세요. Энд хоол битгий идээрэй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">수업 시간에 늦지 마세요. Хичээлээс битгий хоцроорой.</span></p>\r\n', 0);
INSERT INTO `rules` (`r_id`, `rule`, `content`, `level_id`) VALUES
(73, '-아/어도 되다', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Ямар нэгэн үйлдэл хийхийг зөвшөөрөхөд илэрхийлдэг. Үйл үг, тэмдэг үйл үгийн сүүлчийн үенд ㅏ, ㅗ эгшиг орсон бол 아도 되다, ㅏ, ㅗ -аас бусад эгшиг орсон бол 어도 되다 -г тус тус залгана. 하다 -гаар төгссөн үг 해도 되다 хэлбэртэй болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기에 앉아도 돼요? Энд сууж болох уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기서 사진을 찍어도 됩니다. Энд зураг авч болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 지금 집에 가도 돼요? Одоо гэр рүүгээ явж болох уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 집에 가도 돼요. Тийм ээ, гэр рүүгээ явж болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">하숙집에서 요리해도 됩니다. Хөлсний өрөөнд хоол хийж болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 좀 도와 줘도 될까요? Би жаахан туслаад өгөх үү?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 수업 시간에 사전을 봐도 됩니까? Хичээл дээр толь бичиг харж болох уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 봐도 됩니다. Тийм ээ, Болно.</span></p>\r\n', 0),
(74, '보다', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгэнд залгаж, хүн болон эд зүйлсийг харьцуулахад хэрэглэнэ. Монгол хэлнээ харьцуулахын &quot;-аас4&quot; гэсэн утгатай дүйх бөгөөд 더(илүү) 덜(бага) гэсэн үгстэй хамт хэрэглэгдэж болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이 옷보다 저 옷이 더 예뻐요. Энэ хувцаснаас тэр хувцас илүү хөөрхөн.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">버스보다 비행기가 빠릅니다. Автобуснаас онгоц хурдан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">시장이 백화점보다 쌉니다. Зах их дэлгүүрээс хямдхан.</span></p>\r\n', 0),
(75, '-(으)ㄴ - Үйлийн одоо цагийн тодотгон холбох нөхцөл', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тэмдэг үйл үгийн үндсэнд залгаж, хүн олон эд зүйлсийн шинж байдлыг илэрхийлдэг тодотгон холбох нөхцөл. Тэмдэг үйл үгийн үндэс гийгүүлэгчээр төгссөн бол -은, эгшгээр төгссөн бол -ㄴ залгана. Харин тэмдэг үйл үгийн үндэс ㄹ гийгүүлэгчээр төгссөн тохиолдолд ㄹ гийгүүэлчийг гээж ㄴ -ийг залгадаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">큰 가방이 좋아요. Том цүнх сайхан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저 가게에 좋은 물건이 많습니다. Тэр дэлгүүрт гоё бараа их.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">긴 치마를 사고 싶어요. Урт банзал худалдаж авмаар байна.</span></p>\r\n', 0),
(76, '-는 -  Үйлийн одоо цагийн тодотгон холбох нөхцөл', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, тухайн үйлдэл одоо болж байгааг илэрхийлдэг&nbsp;тодотгон холбох нөхцөл. 있다(байх), 앖다(байхгүй) тэмдэг үйл үгийн үндсэнд бас залгаж хэрэглэдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">지금 찾는 장소가 어디예요? Одоо хайж байгаа газар чинь хаанах вэ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어머니가 자주 만드는 음식은 불고기입니다. Ээжийн байнгын хийдэг хоол бол Булгуги.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">멋있는 옷을 한 벌 사고 싶어요. Сайхан хувцас нэг хос худалдаж авмаар байна.</span></p>\r\n', 0),
(77, '-(으)ㄴ - Үйлийн өнгөрсөн цагийн тодотгон холбох нөхцөл', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж тухайн үйлдэл болоод өнгөрсөн утгыг илэрхийлнэ. Үйл үгийн үндэс гийгүүлэгчээр төгссөн бол -은, эгшгээр төгссөн бол ㄴ -ийг тус тус залгана. Харин ㄹ гийгүүлэгчээр төгссөн бол ㄹ -ийг гээж ㄴ -ийг залгадаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">영국에서 온 마이클입니다. Англиас ирсэн Майкл байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제 저녁에 먹은 음식은 불고기였습니다. Өчигдөр орой идсэн хоол Бүлгуги байсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">지난번에 만든 장갑을 친구에게 선물했습니다. Өнгөрсөн удаа хийсэн бээлийгээ найздаа бэлэглэсэн.</span></p>\r\n', 0),
(78, '마다', '<p><span style=\"font-size:14px\">Нэр үгэнд залгаж бүгд, бүхэлдээ гэсэн утга илтгэнэ. Монгол хэлнээ болгон, бүр гэсэн үгтэй утга дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">현우는 수요일마다 도서관에 갑니다. Хён-ү лхагва гараг бүр номын сан явдаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 주말마다 공원에 갑니다. Би хагас бүтэн сайн бүр парк явдаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">가게마다 옷이 다릅니다. Дэлгүүр болгоны (зардаг) хувцас өөр.</span></p>\r\n', 0),
(79, '-아/어야 하다 - \"-х хэрэгтэй, -х ёстой\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгэнд залгаж ямар нэгэн үйлдэл зайлшгүй хийх ёстой илэрхийлнэ. Монгол хэлнээ &quot;-х хэрэгтэй, -х ёстой&quot; гэсэн утгыг илтгэнэ. 하다 -ийн оронд 되다 (болох) гэсэн үгийг хэрэглэж болно.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг болон тэмдэг үйл үгийн үндсийн төгсгөлийн үед ㅏ, ㅗ эгшиг орсон бол -아야, ㅏ, ㅗ -аас бусад эгшиг орсон бол -아여 нөхцөлийг тус тус залгана. Харин 하다 төгсгөлтэй үг -해야 хэлбэртэй болдог.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">병원에 가야 합니다. Эмнэлэг явах ёстой.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">약을 먹고 푹 쉬어야 합니다. Эм ууж амрах хэрэгтэй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">매일 숙제를 해야 합니다. Өдөр бүр гэрийн даалгавараа хийх хэрэгтэй.</span></p>\r\n', 0),
(80, '-지 못하다', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж үйлийн эзэн ямар нэг үйлийг хийхийг хүссэн ч чадахгүй байгаа эсвэл өөр нэг шалтгаанаар тухайн үйл хүссэнээр нь өрнөхгүй утга илэрхийлнэ. Монгол хэлнээ &quot;-ж чадахгүй&quot; гэсэн утгатай дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 운전을 하지 못해요. Би жолоо барьж чаддаггүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제는 친구를 만나지 못했어요. Өчигдөр найзтайгаа уулзаж чадаагүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">배가 아파서 밥을 먹지 못했어요. Гэдэс өвдөөд хоол идэж чадаагүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 한국어를 잘 하지 못해요. Би солонгосоор сайн ярьж чадахгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">감기에 걸려서 학교에 가지 못했어요. Ханиад хүрээд сургуульдаа явж чадаагүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 수영할 수 있어요? Усанд сэлж чадах уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아니요, 수영하지 못해요. Үгүй, сэлж чадахгүй.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><strong>Тодруулга</strong>: -지 못하다 нь ямар нэг зүйл хийж санаа байвч чадвар дутмаг байдлыг илэрхийлдэг бол 지 않다 нь чадвартай боловч хийе гэсэн бодолгүй байхыг илэрхийлнэ.</span></p>\r\n', 0),
(81, '-(으)면', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тодорхой бус, хараахан биелээгүй үйлдлийг таамаглахгүй хэрэглэдэг үйлийн болзох хэлэрийн нөхцөл. Монгол хэлний &quot;-вал4, -бал4&quot; гэсэн нөхцөлтэй дүйцнэ. Үйл үгийн үндэс ㄹ -ээс бусад гийгүүлэгчээр төгсвөл 으면, ㄹ гийгүүлэгчээр болон эгшгээр төгсвөл 면&nbsp;-ийг залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">날씨가 좋으면 여행을 갈 거예요. Цаг агаар сайхан байвал аялалаар явна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">감기에 걸리면 이 약을 드세요. Ханиад хүрвэл энэ эмийг уугаарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">친구들과 같이 살면 재미있을 거예요. Найз нартайгаа хамт амьдарвал сонирхолтой байх болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일 추우면 산에 안 갈 거예요. Маргааш хүйтэн байвал уул руу явахгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국 뉴스를 많이 들으면 한국어를 잘 할 수 있을 거예요. Солонгосын мэдээг сайн сонсвол солонгос хэлээр сайн ярьж чадах болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 시간이 있으면 저랑 영화 보러 갈래요? завтай бол надтай хамт кино үзэхээр явах уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 좋아요. За. Тэгье</span></p>\r\n', 0),
(82, '-(으)ㄹ - Үйлийн ирээдүй цагийн тодотгон холбох нөхцөл', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ирээдүйд болох үйлийг таамаглах утга илэрхийлдэг ирээдүй цагийн үйлийн тодотгон холбох нөхцөл. Монгол хэлний үйлийн ирээдүй цагийн тодотгон холбох &quot;-х&quot; нөхцөлтэй утга дүйнэ. Гийгүүлэгчээр төгссөн үйл үгийн үндсэнд 을, эгшгээр төгссөн үйл үгийн үндсэнд ㄹ -ийг залгана. Харин ㄹ -ээр төгссөн үйл үгийн үндсэнд ㄹ -ийг гээж залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일은 할 일이 많아요. Маргааш хийх ажил их.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">점심에 먹을 음식을 만들었어요. Өдөр идэх хоол хийсэн.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기가 바로 우리가 살 집이에요. Энэ бидний амьдрах гэр.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이게 생일 파티 때 입을 옷이에요. Энэ төрсөн өдрөөр&nbsp;өмсөх хувцас.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일 수업 시간에 들을 내용은 97쪽입니다. Маргаашийн хичээл дээр сонсох агуулга нь 97 дугаар хуудс.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 바트 씨, 제가 도울 일이 있어요? Бат аа, би туслах юм байна уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 손님들이 마실 음료수를 좀 준비해 주세요. Зочидын уух ундааг бэлдээд өгөөч.</span></p>\r\n', 0),
(83, '-아/어서 - \"-аад4, -ж\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж, үйл явдал цаг хугацааны хувьд дэс дараатай өрнөснийг илтгэх холбох нөхцөл. Өмнөх өгүүлбэрийн үйл хараахан дуусалгүй дараагийн өгүүлбэрийн үйлтэй нягт уялдаатай үргэлжлэх утгатай тул үйлийн эзэн нэг байна. Монгол хэлнээ &quot;-аад4, -ж&quot; зэрэг холбох нөхцөлтэй утга дүйнэ. Үйл үг болон тэмдэг үйл үгийн үндсийн сүүлийн үенд ㅏ, ㅗ эгшиг орсон бол -아서, ㅏ, ㅗ -аас бусад эгшиг орсонбол -어서 нөхцөлийг залгах бөгөөд 하다 төгсгөлтэй үг -해서 хэлбэртэй бодлог.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">사관를 씻어서 멋었습니다. Алимыг угааж идсэн.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">도서관에 가서 공부했습니다. Номын санд очоод хичээллэсэн.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">서울역에서 내려서 1호선으로 갈아티세요. Сөүл буудал дээр буугаад нэгдүгээр шугам руу сольж суугаарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">사거러에서 좌화전해서 죽 가세요. Дарвөн замаас зүүн тийш эргэн чигээрээ яваарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저기 연필 좀 주워서 저에게 주세요. Надад тэр харандааг аваад өгөөч.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일 친구를 만나서 쇼핑하러 갈 거예요. Маргааш найзтайгаа уулзаад дэлгүүр хэсэхээр явна.</span></p>\r\n', 0),
(84, '-지요? - \"...биз дээ\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Өгүүлэгч сонсогч этгээдийг мэдэж байгаа гэж бодон асуух буюу эсвэл&nbsp;мэдэж байгаа зүйлийг нь нягтлах зорилгоор асуухад хэрэглэдэг нөхцөл. Монгол хэлнээ &quot;...биз дээ&quot; гэсэн утгатай дүйнэ. Үйл үг болон тэмдэг үйл үгийн үндэс болон өнгөрсөн цагийн -았/었, ирээдүй цагийн -겠 төгсгөл дээр тус тус залгаж хэрэглэдэг. Нэг үгэнд залгах тохиолдолд үндэс нь гийгүүлэгчээр төгссөн бол -이지요, эгшгээр төгссөн бол -지요 -г тус тус залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 그 영화 정말 재미있지요? Тэр кино үнэхээр сонирхолтой биз?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 정말 재미있었어요. Тийм ээ, Үнэхээр сонирхолтой байсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">2시까지 도착할 수 있겠지요? 2 цаг гэхэд ирчихнэ биз?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">거기가 서울백화점이지요? Сөүл их дэлгүүр биз дээ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일 산에 갈 거지요? Маргааш ууланд авирна биз дээ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">지원 씨, 어제 남자랑 영화 봤지요? Живон аа, өчигдөр эрэгтэй хүнтэй кино үзсэн биз?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">체첵 씨는 몽골에서 왔지요? Цэцэг монголоос ирсэн биз дээ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">요즘 매일 학교에 가지요? Ойрд өдөр бүр сургууль дээрээ очиж байгаа биз дээ?</span></p>\r\n', 0),
(85, '서수사 - Дэс тоо', '<p><span style=\"font-size:14px\">Дэс дараалал, давтамжийг илэрхийлнэ.</span></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:800px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\">첫 번째</span></td>\r\n			<td><span style=\"font-size:14px\">두&nbsp;번째</span></td>\r\n			<td><span style=\"font-size:14px\">세&nbsp;번째</span></td>\r\n			<td><span style=\"font-size:14px\">네&nbsp;번째</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\">다섯&nbsp;번째</span></td>\r\n			<td><span style=\"font-size:14px\">여섯&nbsp;번째</span></td>\r\n			<td><span style=\"font-size:14px\">일곱&nbsp;번째</span></td>\r\n			<td><span style=\"font-size:14px\">열&nbsp;번째</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\">스무 번째</span></td>\r\n			<td><span style=\"font-size:14px\">서른&nbsp;번째</span></td>\r\n			<td><span style=\"font-size:14px\">마흔&nbsp;번째</span></td>\r\n			<td><span style=\"font-size:14px\">백&nbsp;번째</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\">...</span></td>\r\n			<td><span style=\"font-size:14px\">...</span></td>\r\n			<td><span style=\"font-size:14px\">...</span></td>\r\n			<td><span style=\"font-size:14px\">마지막</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\">A. 한국에 처음 왔습니까?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아니요, 이번이 두 번째입니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">우리 집은 골목에서 세 번째 집입니다. Манай гэр нарийн гудамжаар ороод гурав дахь гэр нь.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">첫 번째 사거리에서 오른쪽으로 가세요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">오늘이 제 스무 번째 생일이에요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이번이 벌써 열 번째 이야기하는 거예요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 제가 몇 번째입니까?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 여섯 번째니까 조금만 더 기다리세요.</span></p>\r\n', 0),
(86, '-(으)려고', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж үйлийн хүсэл эрмэлзлэлийг илэрхийлнэ. Монгол хэлнээ &quot;-х гэж, -хээр4&quot; гэсэн утгаар буудаг. Үйл үгийн үндэс эгшгээр төгссөн бол -려고, гийгүүлэгчээр төгссөн бол -으려고, ㄹ гийгүүлэгчээр төгссөн бол гийгүүлэгчийг гээж -려고 -г тус тус залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국에서 일하려고 한국어를 배웁니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">사진을 찍으려고 카메라를 가져왔어요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">불고기를 만들려고 고기를 샀어요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">취직하려고 공부합니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국어를 잘 들으려고 매일 한국 드라마를 봅니다. Солонгос хэлээр сайн сонсдог болох гэж өдөр бүр солонгос драм үздэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">친구를 초대하려고 전화했어요.</span></p>\r\n', 0),
(87, '-기 전에 - \"-хийн өмнө\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг үндсэнд залгаж дараагийн өгүүлбэрийн үйлдэл өмнөх өгүүлбэрийн үйлдлээс урьтаж болсоныг илэрхилйнэ. Монгол хэлнээ &quot;-хийн өмнө&quot; гэсэн утгатай дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">자기 전에 숙제를 합니다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">밥을 먹기 전에 손을 씻어야 해요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">고향에 가기 전에 선물을 살 거예요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">수업 시작하기 전에 커피를 마셔요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">화장하기 전에 옷을 입어요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">세수하기 전에 이를 닦아요.</span></p>\r\n', 0),
(88, '-(으)ㄹ게요', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, ямар нэгэн зүйл хийхээр шийдсэн өгүүлбэгчийн хүсэл зориг, үүрэг амлалтыг илэрхийлдэг төгсгөх нөхцөл. Монгол хэлний&nbsp;&quot;-е, -я, -нэ&quot; гэсэн утгатай дүйнэ. Үйлийн эзэн нэгдүгээр биет дээр хэрэглэгддэг. Үйл үгийн үндэс эгшгээр төгссөн бол -ㄹ게요, гийгүүлэгчээр төгссөн бол -을게요, -ㄹ гийгүүлэгчээр төгссөн бол -ㄹ гйигүүлэгчийг нээж -ㄹ게요 -г тус тус залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">학교 앞에서 기다릴게요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 전화를 받을게요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 누가 케이크를 만들 거예요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 제가 만들게요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일은 늦지 않을게요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">공부를 열심히 할게요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">영화 표를 제가 예매할게요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 다음에는 누가 발표를 할까요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 제가 할게요.</span></p>\r\n', 0),
(89, '-겠-', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж таамаглал дэвшүүлэх санааг илэрхийлнэ. Хүндэтгэлийн -시-, үйл үгийн өнгөрсөн цагийн -았/었- нөхцөл орсон тохиолдолд эдгээр нөхцөлийн ард тус тус залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">하늘에 구름이 많아요. 내일 비가 오겠어요. Тэнгэрт үүл их байна. Маргааш бороо орох нь.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">선생님은 기쁘시겠다. Багш баярлах байх даа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 어제는 정말 바빴겠어요. Өчигдөр маш их завгүй байж ээ дээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 네, 하지만 지금은 괜찮아요. Тийм ээ, гэхдээ одоо зүгээр.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">정말 맛있겠어요! Үнэхээр амттай байх аа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">시험을 잘 봐서 좋겠어요. Шалгалтаа сайн өгөөд их сайн байгаа байх даа.</span></p>\r\n', 0),
(90, '-네요', '<p><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж яригч тухайн зүйлийг шинээр мэдэж авч гайаж байгааг илэрхийлнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국말을 정말 잘하네요. Солонгос хэлээр үнэхээр сайн ярих юм аа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">가방이 아주 예쁘네요. Цүнх чинь их хөөрхөн юм аа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">오늘은 날씨가 춥네요. Өнөөдөр цаг агаар их хүйтэн байх чинь.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이 옷은 정말 싸네요. Энэ хувцас хямдхан юм аа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국어 시험이 생각보다 어렵네요. Солонгос хэлний шалгалт санаснаас хүнд байна шүү.</span></p>\r\n', 0),
(91, '-아/어 보다', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, ямар нэгэн үйлдлийг хийж үзэх, оролдох, турших санааг илэрхийлнэ. Монгол хэлнээ &quot;-ж үзэх&quot; гэсэн утгатай дүйнэ. Үйл үгийн үндсийн сүүлийн үенд ㅏ, ㅗ эгшиг орсон байвал -아 보다, ㅏ, ㅗ -аас бусад эгшиг орсон байвал -어 보다 -г тус тус залгана. 하다 -гаар төгссөн үйл үг -해 보다 хэлбэртэй болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제주도에 한번 가 보세요. 정말 아름다워요. Чэжү&nbsp;аралд очоод үзээрэй. Үнэхээр үзэсгэлэнтэй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이 사과가 아주 맛있네요. 한번 드셔 보세요. Энэ алим их амттай юм аа. Идээд үз.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 민준 씨, 바다에서 낚시를 해 봤어요? Минжүн ээ, далайгаас загас барьж үзсэн үү?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아니요, 하지만 강에서는 해 봤어요. Үгүй. Харин голын загас барьж үзсэн.</span></p>\r\n', 0),
(92, '(으)나', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Хоёр болон түүнээс дээш нэрийг тоочих, эсвэл аль нэгийг сонгох утга илтгэдэг. Тухайн нэрийн үндэс гийгүүлэгчээр төгсвөл 이나, эгшгээр төгсвөл 나 хэлбэртэй залгана. Монгол хэнлний &quot;...эсвэл, ...юм уу&quot; хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이번 토요일이나 일요일에는 영화를 보려고 해요. Энэ хагас сайн юм уу бүтэн сайн өдөр кино үзэх санаатай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">학교에 갈 때 버스나 지하철을 타요. Сургууль руугаа автобус, эсвэл метронд сууж явдаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">휴일에 산이나 바다로 여행을 갈 거예요. Амралтаараа уул юм уу далайгаар аялана даа.</span></p>\r\n', 0),
(93, '-(으)면 좋겠다', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тухайн үйлийг биелээсэй хэмээн хүсэмжилсан, эсвэл одоогийн бодит байдлаас өөрөөр эргэхийг хүссэн утга илтгэнэ. Үйлийн үндэс гийгүлэгчээр төгсвөл -으면 좋겠다, эгшгээр төгсвөл -면 좋겠다 хэлбэр залгадаг. Мөн үйлийн үндэс ㄹ гийгүүлэгчээр төгссөн тохиолдолд -면 좋겠다 хэлбэрийг залгана. Монгол хэлний &quot;-аасай4 билээ, -вал4 сайн сан, -сан бол, -х юмсан&quot; гэх мэт хэлбэртэй дүйдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저한테 형이 있으면 좋겠어요. Би ахтай байсал бол сайхан аа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일 비가 안 오면 좋겠어요. Маргааш бороо орохгүй бол сайнсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">다음 학기에는 학교 기숙사에서 살면 좋겠어요. Дараагийн улиралд сургуулийнхаа байранд амьдрах юмсан.</span></p>\r\n', 0),
(94, '-기 위해서', '<p><span style=\"font-size:14px\">Үйл үгэнд залгаж үйлийн санаа зорилгыг илтгэнэ. Монгол хэлний &quot;-н төлөө, -х гэж, -х санаатай&quot; гэсэн хэлбэртэй утга дүйдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">김치를 만들기 위해서 배추를 샀어요. Кимчи хийх гэж цэцгэн байцаа худалдаж авлаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">통역사가 되기 위해서 한국어를 배워요. Орчуулагч болох санааай солонгос хэл сурч байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">세계 여행을 하기 위해서 돈을 모았어요. Дэлхийгээр тойрон аялах гэж мөнгө цуглуулсан юм.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Тодруулга</strong>. Үйл үгийн үндсэнд 기 위해서 хэлбэр залгадаг бол нэр үгийн үндсэнд 을/를 위해서 хэлбэрийг залгана.</span></p>\r\n', 0),
(95, '-아/어지다', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тэмдэг үйл үгийн ард залган тухайн үйл өөрчлөгдөн буй төлөвийн утга илтгэнэ. Харин хөдлөл заасан үйл үгэнд залгавал ямар нэгэн үйлдэл хийх болсон эсвэл тухайн үйл аяндаа бий болсон төлөв байдлын утгатай. Монгол хэлний &quot;... болох, ... сан4 байх&quot; гэх мэт төлөв байдлын утгатай дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">뭉흐 씨 얼굴이 예뻐졌어요. Мөнх улам царайлаг болжээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제보다 기분이 좋아졌어요. Өчигдөрийг бодвол сэтгэл санаа дээшиллээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">요즘 날씨가 많이 더워졌어요.&nbsp;Ойрдоо цаг агаар их дулаарлаа.</span></p>\r\n', 0),
(96, '-기 때문에', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үг болон тэмдэг үйл үгийн ард залгах өмнөх өгүүлбэр нь арын өгүүлбэрийнхээ учир шалтгаан болох утгатай. Захирах, шаардан хүсэх өгүүлбэрт хэрэглэдэггүй. Монгол хэлний &quot;... учир, ... болохоор&quot; гэх мэт утгатай дүйлгэж болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">오늘은 비가 오기 때문에 등산을 안 가요. Өнөөдөр бороо орох учраас ууланд гарахгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제 몸이 아팠기 때문에 학교에 못 갔어요. Өчигдөр бие өвдөөд сургуульдаа очсонгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">외국인이기 때문에 한국말을 잘 못 해요. Гадаад хүн болохоор солонгос хэл сайн мэддэггүй юм.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Жич: Нэг үг, тэмдэг нэрийн ард 때문에 (учир) хэлбэрийг шууд залгаж хэрэглэнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">감기 때문에 집에서 쉬어요. Ханиадтай болохоор гэртээ амарч байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">요즘 그 일 때문에 아주 바빠요. Ойрд тэр хэргээс болж зав муутай байна.</span></p>\r\n', 0),
(97, 'ㅎ дүрмийн бус хувирал', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">ㅎ дэвсгэр үсгээр төгссөн тэмдэг үйл үг дундаас өмнө нь, зүг чиг заасан тэмдэг үйл үгийн үндсэнд эгшгээр эхэлсэн загавар залгавал ㅎ гийгүүлэгч гээгддэг. Гэхдээ -아/어 эгшгээр эхэлсэн дагавар залгавал ㅎ гйигүүлэгч гээгдэн ㅐ хэлбэрээр хувирна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">빨갛다: 빨가 + -아요 -&gt; 빨개요</span></p>\r\n\r\n<p><span style=\"font-size:14px\">빨간 장미가 예뻐요. Улаан сарнай нь гоё&nbsp;байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">가을 하늘이 높고 파래요. Намрын тунгалаг болоо өндөр тэнгэр.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 까만객보다 하얀색을 더 좋아해요. Би хар өнгөнөөс цагаан өнгөнд илүү дуртай.</span></p>\r\n', 0),
(98, '-거나 - юм уу, аль эсвэл', '<p><span style=\"font-size:14px\">Хоёр болон түүнээс дээш үйлийн аль нэгийг сонгох утга илтгэх холбох нөхцөл. Монгол хэлний &quot;юм уу, аль эсвэл&quot; гэсэн үгтэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">부모님께 전화를 하거나 이메일을 보내세요. Эцэг эх рүүгээ утсаар ярих юм уу, имэйл бичээрэй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">수업이 끝나면 숙제를 하거나 도서관에 가요. Хичээл тарахаар даалгавраа хийх юм уу, номын санд очдог.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">주말에 집에서 책을 읽거나 친구와 영화를 봐요. Хагас бүтэн сайнаар ном унших юм уу, найзтайгаа кино үздэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">자기 전에 드라마를 보거나 인터넷을 해요. Унтахаасаа өмнө тележүжиг үзэх юм уу интернэтээр ордог.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">시간이 있을 때 집에서 쉬거나 친구와 쇼핑해요.&nbsp;Завтай үедээ гэртээ амрах юм уу найзтайгаа дэлгүүр хэсдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">점심 때 비빔밥을 먹거나 라면을 먹어요.&nbsp;Өдрийн хоолонд бибимбаб юм уу бэлэн гоймон иддэг.</span></p>\r\n', 0),
(99, '-고 있다 - ж/ч байгаа/байна', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн ард залган одоо үргэлжлэх цагийн утга илтгэдэг. Цаг заах төлвийн нөхцөлийг 고 -ийн ард биш харин 있 -ийн ард залгах бөгөөд эзэн биеийг хүндэтгэх тохиолдолд 고 계시다 хэлбэр ашиглана. Монгол хэлний &quot;-ж/ч байгаа/байна&quot; гэсэн утгатай дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">우리는 한국어를 공부하고 있어요. Бид солонгос хэлийг сурч байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">민재 씨가 운동장에서 농구를 하고 있어요. Минжэ биеийн тамирын талбайд сагс тоглож байгаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">아버지께서는 지금 전화를 받고 계세요. Аав одоо утсаар ярьж байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">요즘 테니스를 배우고 있어요. Сүүлийн үед теннис сурч байгаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">동생은 지금 음악을 듣고 있어요. Дүү одоо хөгжим сонсож байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">할아버지께서는 책을 읽고 계세요. Өвөө ном уншиж байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 수빈 씨, 전화를 왜 계속 안 받았어요? Сүбин аа, утсаа яагаад авахгүй байсан бэ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 미안해요. 청소를 하고 있었어요. Уучлаарай, Цэвэрлэгээ хийж байсан юм.</span></p>\r\n', 0),
(100, '-(으)ㄴ 적이 있다/없다 - ж байсан ... сан удаатай, - ж байгаагүй ... сан удаагүй', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үгийн ард залган тухайн үйлийг урьд өмнө нь хийж, туршиж байснаа илэрхийлэх утгатай. Хэрвээ энэ талаар туршлага үгүй бол -(으)ㄴ 적이 없다 гэвэл болно. Монгол хэлний &quot;-ж байсан ... сан удаатай&quot; &quot;-ж байгаагүй ... сан удаагүй&quot; гэсэн утгатай дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">고등학교 때 그 책을 읽은 적이 있어요. Ахлах сургуульд байхдаа тэр номыг уншиж байсан юм байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 민재 씨 동생을 만난 적이 있어요. Би Минэжгийн дүүтэй уулзаж байсан юм байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 한국에 가 본 적이 없어요. Би Солонгост очиж байгаагүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">아주 많이 운 적이 있어요? Маш их уйлж байсан удаа бий юу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">선생님 질문을 잘 못 들은 적이 있어요. Багшийн асуултыг буруу сонож байсан удаа бий.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">길에서 외국인을 도와 준 적이 있어요. Гудамд гадаадын хүнд тусалж байсан юм байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 불고기를 먹어 본 적이 있어요? Бүлгуги идэж үзсэн үү?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아니요, 아직 먹어 본 적이 없어요. Үгүй, одоохондоо идэж үзээгүй л байна.</span></p>\r\n', 0),
(101, '-(으)면서 - нгаа4, сацуу', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залган хоёр ба түүнээс дээш үйлийн зэрэгцэж болох утга илтгэнэ. Эгшгээр төгссөн үйлийн үндсэнд -면서, гийгүүүлэгчээр төгссөн үйлийн үндсэнд -으면서 хувилбарыг залгана. Мөн ㄹ гийгүүлэгчээр төгссөн бол -면서 хэлбэрийг залгадаг. Монгол хэлний &quot;-нгаа4, -н сацуу&quot; хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">커피를 마시면서 친구와 이야기를 합니다. Кофе уунгаа найзтайгаа ярьдаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">신문을 읽으면서 아침을 먹습니다. Сонин уншингаа өглөөний цай уудаг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">청소하면서 음악을 들어요. Цэвэрлэгээ хийнгээ хөгжим сонсдог.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">영화가 너무 슬퍼서 울면서 봤어요. Кино их гунигтай байсан болохоор уйлсаар үзлээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">쓰레기를 주우면서 산책했어요. Хог түүж цэвэрлэсээр салхиллаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">공원을 걸으면서 생각을 합니다. Паркаар алхангаа юм боддог.</span></p>\r\n', 0),
(102, '요', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Нэр үг, туслах үг, үйлийн холбох нөхцөлд залган асуултанд товч тодорхой хариулах тохиолдолд хэрэглэдэг хүндэтгэлийн нөхцөл. Монгол хэлэнд дүйцэх нөхцөл байхгүй учир харьцагч этгээдээ хүндэлж байгаагаа аялгаараа илтгэхээс өөр аргагүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 어제 어디에 있었어요? Өчигдөр хаана байсан бэ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 집에요. Гэртээ</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 어제 왜 학교에 안 왔어요? Өчигдөр яагаад хичээлдээ ирээгүй вэ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 감기에 걸려서요. Ханиад хүрээд</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 이거 어디에서 샀어요? Үүнийг хаанаас авсан бэ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 백화점에서요. Их дэлгүүрээс.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 누구랑 영화 봤어요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 지원 씨요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 무슨 영화 봤어요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 배트맨이요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 언제 집에 돌아왔어요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 밤 10시요.</span></p>\r\n', 0),
(103, '-(으)면 안 되다 - ж,ч болохгүй, -вал4 болохгүй', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залган хориглох утга илтгэнэ. Үйлийн үндэс эгшгээр төгссөн бол -면 안 되다, гийгүүлэгчээр төгссөн бол -으면 안 되다хэлбэрийг хэрэглэнэ. Мөн ㄹ гийгүүлэгчээр төгссөн үйлийн үндсэнд -면 안 되다 орно. Монгол хэлний &quot;-ж,ч болохгүй, -вал4 болохгүй&quot; хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기서 담배를 피우면 안 됩니다. Энд тамхи татаж болохгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">수업 시간에 늦으면 안 됩니다. Хичээлд хоцорч ирж болохгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 기숙사에서 요리를 해도 돼요? Нийтийн байранд хоол хийж болох уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아니요, 기숙사에서 요리를 하면 안 돼요. Үгүй, нийтийн байранд хоол хийж болохгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">컴퓨터실에서 음료수를 마시면 안 됩니다. Компьютерын өрөөнд ундаа ууж болохгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이 옷은 팔면 안 돼요. Энэ хувцас зарж болохгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">음악을 들으면 안 됩니다. хөгжим сонсож болохгүй.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 미술관에서 사진을 찍어도 됩니까? Дүрслэх урлагийн музейд зураг дарж болох уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아니요, 사진을 찍으면 안 됩니다. Үгүй, зураг дарж болохгүй.</span></p>\r\n', 0),
(104, '(이)나 - бүр, ч', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Тоо хэмжээ нь санаснаас их байгааг онцлох утга илэрхийлнэ. Үйлийн үндэс&nbsp;гийгүүлэгчээр төгсвөл 이나, эгшгээр төгсвөл 나-г залгадаг. Монгол хэлэнд дүйцэх нөхцөл байхгүй ч &quot;бүр&quot;, &quot;ч&quot; гэх мэт дайвар үг болон дагалдах чимэг үгийн тусламжтайгаар дээрх утгыг гаргаж болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">커피를 4잔이나 마셨어요. Кофе бүр дөврөн аягыг уусан шүү.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">장미꽃을 20송이나 샀어요. Сарнай бүр хорийг авсан.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">토야 씨는 저보다 5살이나 어립니다. Туяа надаас бүр 5 насаар дүү.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">어제 한국어 공부를 6시간이나 했어요. Өчигдөр солонгос хэлний хичээл 6 цаг орчим хийлээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">요즘 한국에서는 보통 커피가 5,000원이나 해요. Сүүлийн үед Солонгост энгийн кофе 5000 орчим вон болоод байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 바트 씨, 1시간이나 늦었어요! Бат аа, 1 цаг хэртэй хоцорлоо!</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 정말 미안해요. Үнэхээр уучлаарай.</span></p>\r\n', 0),
(105, '(으)ㄹ 것 같다 - юм шиг байх, -х бололтой', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Одоо, ирээдүй, өнгөрсөн цагт болсон болон болох хэргийг таамаглах утга илтгэнэ. Үйлийн үндэс гийгүүлэгчээр төгсвөл -을 것 같다, эгшгээр болон ㄹ гийгүүлэгчээр төгсвөл -ㄹ 것 같다 хэлбэрийг залгана. Монгол хэлний &quot;юм шиг байх, -х бололтой&quot; хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저 분은 선생님일 것 같아요. Тэр хүн багш юм шиг байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일은 비가 안 올 것 같아요. Маргааш бороо орох бололтой.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이제 어머니의 마음을 알 것 같아요. Одоо л ээжийнхээ сэтгэлийг ойлгож байх шиг байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">케이크가 정말 맛있을 것 같아요. Бялуу их амттай харагдаж байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저 사람은 중국 사람일 것 같아요.Тэр хүн хятад хүн юм шиг байна.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">설렁거 씨는 시험 때문에 내일 도서관에 갈 것 같아요. Солонго шалгалттай болохоор маргааш номын санд очих байлгүй.</span></p>\r\n', 0),
(106, 'ㅅ дүрмийн бус хувирал', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">ㅅ гийгүүлэгчээр төгссөн 낫다, 짓다, 붓다, 젓다 гэх мэт үйл үгийн үндсэнд эгшгээр эхэлсэн дагавар залгавал ㅅ дэвсгэр үсэг гээгдэнэ. Гэхдээ 씻다, 웃다, 벗다 гэх мэт үйл үгийн үндсэнд эгшгээр эхэлсэн дагавар залгахад ㅅ гийгүүлэгч гээгдэхгүй тул эхнийхийг дүрсийн бус хувилал, дараагийнхыг дүрминй хувилал гэдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">낫다: 낫 + 아요 -&gt; 나아요</span></p>\r\n\r\n<p><span style=\"font-size:14px\">약을 먹고 감기가 나았어요. Эм ээж ханиад эдгэрэв.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">얼굴이 좀 부었어요. Нүүр бага зэрэг хөөчихөж</span></p>\r\n\r\n<p><span style=\"font-size:14px\">바야르 씨는 항상 웃어요. Баяр дандаа инэээг</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기에는 쇼핑센터를 지을 거예요. Энд худалдааны төв барих байх</span></p>\r\n\r\n<p><span style=\"font-size:14px\">커피를 잘 저어서 드세요. Кофегоо сайн хутгаж уугаарай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">더우니까 코트를 좀 벗으세요. Халуун байна, гадуур хувцсаа тайлал даа.</span></p>\r\n', 0),
(107, '-(으)ㄴ 지 - аас4 хойш', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж тухайн нэг үйлдлийн гүйцэтгэснээс хойш хэр зэрэг хугацаа өнгөрснийг илтгэх утгатай. Эгшгээр төгссөн үйлийн үндсэнд -ㄴ 지, гийгүүлэгчээр төгссөн бол -은 지 хэлбэрийг залгана. Ихэвчлэн -(으)ㄴ 지 (시간이) 되다 хэлбэрийг өргөн ашигладаг. Монгол хэлний &quot;-аас4 хойш&quot; хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국어를 배운 지 육 개월이 됐습니다. Солонгос хэл сурч эхлээд зургаан сар боллоо.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">점심을 먹은 지 두 시간쯤 됐어요. Өдрийн хоол идсэнээс 2 цаг орчим болсон.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">고등학교를 졸업한 지 벌써 십 년이 됐습니다. Ахлах сургуулиа төгссөнөөс хойш 10 жил болжээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국 노래를 안 들은 지 일 년이 지났습니다. Солонгос дуу сонсолгүй нэг жил өнгөрчээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">여기에서 산 지 삼 개월쯤 됐어요. Энд амьдраад 3 сар хэртэй боллоо.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">담배를 안 피운 지 사 년이 지났습니다. Тамхи таталгүй дөрвөн жил өнгөржээ.</span></p>\r\n', 0),
(108, '-(으)ㄴ 후에 - н дараа', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, нэг үйл дуусгасны дараа дараагийн үйл эхлэх утга илтгэдэг. Үйлийн үндэс эгшгээр төгсвөл -ㄴ 후에, гийгүүлэгчээр төгсвөл -은 후에 хэлбэр залгана. Монгол хэлний &quot;-н дараа&quot; хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">점심을 먹은 후에 차를 마셨습니다. Өдрийн хоол идсэний дараа цай уулаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">수업이 끝난 후에 친구를 만났어요. Хичээл тарсны дараа найзтайгаа уулзав.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">졸업한 후에 한국에 가려고 합니다. Сургуулиа төгссөний дараа Солонгос явах санаатай.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">세수를 한 후에 옷을 입습니다. Нүүр гараа угаасны дараа хувцсаа өмсдөг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">친구의 이야기를 들은 후에 생각해 보겠습니다. Найзынхаа яриаг сонссоны дараа бодож үзье.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">샌드위치를 만든 후에 우유랑 먹었습니다. Хачиртай талх хийснийхээ дараа сүүтэй хамт идлээ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Тодруулга</strong>: Мөн ойролцоо утгатай -(으)ㄴ 다음에 хувилбарыг хэрэглэж болно.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">예) 점심을 먹은 다음에 차를 마셨습니다. Жишээ нь: Өдрийн хоол идсэний дараа цай уулаа.</span></p>\r\n', 0),
(109, '-기로 하다 - аар4 болох', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйлийн үндсэнд залган тухайн үйлийг хийхээр шийдсэн, эсвэл хийхээр амлаж буй утга илтгэдэг. Ихэвчлэн -기로 했다 хэлбэрийг ашиглана. Монгол хэлний &quot;-аар4 болох&quot; хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내년에 한국에 가기로 했어요. Ирэх жил Солонгос явахаар болсон</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일부터 운동을 열심히 하기로 했습니다. Маргаашнаас биеийн тамир хийхээр боллоо.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">학교 앞에서 만나기로 해요. Сургуулийн өмнө уулзахаар болсон.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">주말에 가족들과 식사를 하기로 해서 시간이 없을 것 같아요. Хагас бүтэн сайнаар гэрийхэнтэйгээ хамт хооллохоор ярьчихсан, зав гаргахгүй байхаа.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 지원 씨랑 영화 보기로 했는데 같이 갈래요? Живентай хамт кино үзэхээр болсон, хамт явах уу?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 아, 저는 어머니랑 백화점에 가기로 했어요. Өө, Би ээжтэйгээ их дэлгүүр явахаар тохирчихсон.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">A. 민재 씨, 우리가 언제 밥 먹기로 했지요? Минжэ ээ, бид хэзээ хамт хоол иднэ гэлээ?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">B. 저녁 6시에 먹기로 했어요. Орой 6 цагт иднэ гэсэн.</span></p>\r\n', 0),
(110, '에게서, 한테서', '<p>Нэр үгийн ард залган юм үзэгдлийн гарах, эхлэх утга илтгэх тийн ялгалын нөхцөл. 한테서 хэлбэрийг ихэвчлэн ярианы хэлэнд хэрэглэдэг. 에게, 한테 гэх мэтээр 서 -г хураангуйлсан хэлбэр мөн өргөн хэрэглэгдэнэ. Монгол хэлний гарахын тийм ялгалын &quot;аас4&quot; нөхцөлтэй дүйнэ.</p>\r\n\r\n<p>동생에게(서) 선물을 받았습니다.</p>\r\n\r\n<p>고향 친구한테(서) 전화가 왔습니다.</p>\r\n\r\n<p>그 이야기는 친구에게(서) 들었어요.</p>\r\n\r\n<p>언니에게(서) 이메일을 받았아요.</p>\r\n\r\n<p>선생님께 책을 빌렸습니다.</p>\r\n\r\n<p>제 생일낭 부모님께 용돈을 받았습니다.</p>\r\n\r\n<p><strong>Тодруулга:</strong>&nbsp;Хүндэтгэлийн хэлбэрээр 께 нөхцөл ордог. Жишээ нь:</p>\r\n\r\n<p>예) 어머니께 전화를 받았아요. Жишээ нь: Ээж утсаар ярьсан.</p>\r\n', 0),
(111, '-(으)ㄴ/는데', '<p>Цаашид тайлбарлан хэлэх зүйлээ урьдчилж товч дурдах тохиолдолд энэ нөхцөлийг хэрэглэдэг. Ялангуяа санал хүсэлт тавихынхаа өмнө урьдтал нөхцөлөө тайлбарлахад хэрэглэнэ. Үйл үгийн үндсэнд, эсвэл 있다, 없다 зэрэг үгсийн ард -는데, харин тэмдэг үйл үгийн үндсэнд -(으)ㄴ데 хэлбэр залгана. Өнгөрсөн цагийн нөхцөлтэй хамт орвол -았/었는데 хэлбэртэй болно. Монгол хэлний дүйцэх нөхцөл үгүй учир тухайн өгүүлбэрийн хам утгаар &quot;шүү, л даа4&quot; гэх мэт орчуулж эл утгыг гаргаж болно.</p>\r\n\r\n<p>한국말을 잘 모르는데 이것 좀 가르쳐 주세요. Солонгос хэл сайн мэддэггүй, энэ юу гэсэн үг вэ?</p>\r\n\r\n<p>날씨가 좋은데 공원에 놀러 갈까요? Цаг агаар сайхан байна шүү, парк явж тоглох уу?</p>\r\n\r\n<p>숙제가 어려운데 좀 도와주세요. Даалгавар хэцүү байна л даа, надад туслаарай.</p>\r\n\r\n<p>어제 백화점에 갔는데 영화배우를 봤아요. Өчигдөр их дэлгүүрт очсон чинь, кино жүжигчинтэй таарсан.</p>\r\n\r\n<p>방학 때 가족과 같이 여행했는데 정말 즐거웠어요. Амралтаар гэрийхэнтэйгээ аяллаар явсан чинь үнэхээр хөгжилтэй байсан шүү.</p>\r\n\r\n<p>선생님, 질문이 있는데 좀 가르쳐 주시겠어요? Багш аа, асуулт байна, та зааж өгөхгүй биз?</p>\r\n\r\n<p><strong>Жич:</strong>&nbsp;Өмнөх өгүүлбэрийн утгаас эсрэг үр дүн гарах, эсвэл эсрэгцсэн утга бүхий нийлмэл өгүүлбэрт хэрэглэнэ.</p>\r\n\r\n<p>밥을 먹었는데 배가 앞아요. Хоол идсэн чинь гэдэс өвдөж байна.</p>\r\n\r\n<p>동생은 키가 큰데 저는 키가 작아요. Дүү өндөр боловч харин би намхан.</p>\r\n', 0),
(112, '르 дүрмийн бус хувирал', '<p>르 -ээр төгссөн үйл үгийн үндсэнд -아/어요, -아/어서 гэх мэт 아/어 эгшгээр эхэлсэн нөхцөл залгахад ㅡ эгшиг гээгдэн, ㄹ гийгүүлэгч нэмэгддэг. 르 -ийн өмнөх үед ㅏ, ㅗ эгшиг орсон бол -아요 хэлбэр, ㅏ,ㅗ -аас бусад эгшиг орсон бол -아요 хэлбэрийг залгана.</p>\r\n\r\n<p>모르다: 몰ㄹ+아요 -&gt; 몰라요</p>\r\n\r\n<p>부르다: 불ㄹ+어요 -&gt; 불러요</p>\r\n\r\n<p>다 함께 생일 축하 노래를 불렀어요.</p>\r\n\r\n<p>저와 동생은 성격이 달라요.</p>\r\n', 0),
(113, '-나요?/-(으)ㄴ가요?', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг болон тэмдэг үйл үгийн үндсэнд залгаж, сонсогчоо хүндэтгэж аливаа зүйлийг асуухад хэрэглэдэг. Монгол хэлэнд &quot;та -сэн4 үү, -хгүй юү?, -дэг4 үү&quot; гэх зэрэг утгатай дүйнэ. Одоо цагийн хэлбэрт үйл үгийн үндсэнд -나요?, тэмдэг үйл үгийн үндсэнд -(으)ㄴ가요? -г, өнгөрсөн цагийн хэлбэрт үйл үг, тэмдэг үйл үгийн үндсэнд адилхан -았/었나요? -г, ирээдүй цагийн хэлбэрт -(으)ㄹ 건가요? -г тус тус залгана.</span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\" style=\"text-align: center; width: 300px; height: 40px;\">&nbsp;</th>\r\n			<th scope=\"col\" style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">현재</span></th>\r\n			<th scope=\"col\" style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">과거</span></th>\r\n			<th scope=\"col\" style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">미래</span></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">동사</span></td>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">-나요?</span></td>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">-았/었나요?</span></td>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">-(으)ㄹ 건가요?</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">형용사</span></td>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">-(으)ㄴ가요?</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">명사</span></td>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">인가요?</span></td>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">이었/였나요?</span></td>\r\n			<td style=\"text-align: center; width: 300px; height: 40px;\"><span style=\"font-size:16px\">-</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\">이번 모임에 오시나요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">요즘도 많이 바쁘신가요?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">요즘 몽골 사람들은 무슨 노래를 좋아하나요?</span></p>\r\n', 0),
(114, '-는 게 좋다 - \"-х нь сайн, -х нь илүү, -х  нь зүгээр\"', '<p>Үйл үг болон 있다, 없다 үгийн үндсэнд залгаж, тухайн үйлийг хийх нь сайн, илүү талаар зөвлөгөө өгөхөд хэрэглэнэ. Монгол хэлнээ &quot;-х нь сайн, -х нь илүү, -х&nbsp; нь зүгээр&quot; гэсэн утгатай дүйнэ. -는 것이 좋다 хэллэгийн товчилсон хэлбэр бөгөөд -는 게 좋다 нь санаагаа эрс, шууд иэлрэхийлэхэд хэрэглэдэг бол -는 게 좋겠다 нь болгоомжилж зөвлөсөн санааг тус тус илэрхийлнэ.</p>\r\n\r\n<p>지금 가는 게 좋겠어요.</p>\r\n\r\n<p>열이 날 때는 병원에 가는 게 좋아요.</p>\r\n\r\n<p>피곤할 때는 집에 가서 쉬는 게 좋아요.</p>\r\n', 0),
(115, '-아/어 보이다', '<p>Тэмдэг үйл үгийн үндсэнд залгаж, ямар нэг үйлийн талаар төсөөлөх эсвэл дүгнэх утга илэрхийлнэ. Монгол хэлнээ &quot;-ж харагдах&quot; гэсэн утгатай дүйцнэ.</p>\r\n\r\n<p>이 책은 아주 어려워 보여요.</p>\r\n\r\n<p>이 교실은 좁아서 좀 답답해 보여요.</p>\r\n\r\n<p>지원 씨는 대학생인데 너무 어려 보여요.</p>\r\n', 0),
(116, '-(으)ㄴ/는 것 같다', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тэмдэг үйл үг болон үйл үгийн үндсэнд залгаж, ямар нэгэн үйл явдлын өрнөлийг тааварлах үед хэрэглэнэ. Монгол хэлний &quot;-ж байгаа юм шиг байна&quot; гэсэн хэллэгтэй утга дүйцнэ. Голдуу бодол санаагаа шууд хэлэхгүй эерүүлж хэлэхэд хэрэглэдэг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Эгшиг болон ㄹ гийгүүлэгчээр төгссөн бол тэмдэг үйл үг болон 이다, 아니다 туслах үйл үгийн үндсэнд -ㄴ 것 같다, ㄹ-ээс бусад гийгүүлэгчээр төгссөн тэмдэг үйл үгийн үндсэнд -은 것 같다, үйл үг болон 있다, 없다&nbsp; үгийн үндсэнд -는 것 같다 -г тус тус&nbsp; залгана.</span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 300px;\"><span style=\"font-size:14px\">동사</span></td>\r\n			<td style=\"text-align: center; width: 300px;\"><span style=\"font-size:14px\">있다, 없다</span></td>\r\n			<td style=\"text-align: center; width: 300px;\"><span style=\"font-size:14px\">형용사</span></td>\r\n			<td style=\"text-align: center; width: 300px;\"><span style=\"font-size:14px\">이다</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 300px;\"><span style=\"font-size:14px\">-는 것 같다</span></td>\r\n			<td style=\"text-align: center; width: 300px;\"><span style=\"font-size:14px\">-는 것 같다</span></td>\r\n			<td style=\"width: 300px;\">\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-ㄴ 것 같다</span></p>\r\n\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-은 것 같다</span></p>\r\n			</td>\r\n			<td style=\"text-align: center; width: 300px;\"><span style=\"font-size:14px\">-인 것 같다</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\">최정우 씨가 많이 아픈 것 같다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">유진 씨가 감기에 걸려서 집에서 쉬는 것 같다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저 사람은 학생은 것 같다.</span></p>\r\n', 0),
(117, '대신(에) - \"-ний оронд\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгэнд залгаж, ямар нэгэн зүйлийн байрыг солих юм уу ямар нэг зүйлийн хийх ажлыг сольж хэлэхэд хэрэглэх бөгөөд монгол хэлнээ &quot;-ний оронд&quot; гэсэн утгатай дүйнэ. Зарим тохиолдолд 에 -г хураангуйлж хэрэглэх нь элбэг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">커피 대신(에) 우유로 주세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">선물 대신(에) 용돈을 보냈어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">치마 대신(에)바지로 교환해 주세용.</span></p>\r\n', 0),
(118, '-(으)ㄴ/는 대신에', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг болон тэмдэг үйл үгийн үндсэнд залгаж, өмнөх үйлдлийн оронд өөр үйлдэл хийх буюу өмнөх байдлаас өөр байдлыг илэрхийлэхэд хэрэглэж, монгол хэлнээ &quot;-хийн оронд&quot; гэсэн утгатай дүйцнэ. Зарим тохиолдолд 에 -г хураангуйлж болох бөгөөд эгшгээр төгссөн тэмдэг үйл үгийн үндсэнд -ㄴ 대신에, гийгүүлэгчээр төгссөн тэмдэг үйл үгийн үндсэнд -은대신에, үйл үгийн үндсэнд -는 대신에 -г тус тус залгана.</span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">&nbsp;</td>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">현재</td>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">과거</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">동사</td>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">-는 대신에</td>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">-ㄴ 대신에 / -은 대신에</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">형용사</td>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">-ㄴ 대신에 / -은 대신에</td>\r\n			<td style=\"text-align: center; width: 800px; height: 40px;\">-</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\">음악회에 가는 대신(에) 영화를 보는 게 좋겠어요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">그 옷은 싼 대신(에) 품질이 좋지 않아요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제가 늦은 대신(에)맛있는 음식을 사 드릴게요.</span></p>\r\n', 0),
(119, '-기는 하다', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг болон тэмдэг үйл үгийн үндсэнд залгаж, өгүүлэгчийн ярианы зарим хэсэгтэй нь санал нийлж байгаа ч заримтай нь санал нийлэхгүй, үгүйсгэж байгаа санааг илэрхийлнэ. Монгол хэлнээ &quot;-х -нь&quot;, &quot;... ч&quot; гэсэн утгатай дүйнэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">마음에 들기는 하지만 좀 비싸요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">식사를 하기는 했는데 또 배고파요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">이 바지가 예쁘기는 하지만 허리가 좀 큰 것 같아요.</span></p>\r\n', 0),
(120, '-고 나서 - сэний дараа, ж дуусгаад', '<p>Үйл үгийн үндсэнд залгаж, өмнөх үйлдэл дууссаны дараагаар дараагийн үйлдэл хийхийг илэрхийлэх бөгөөд монгол хэлнээ &quot;-сэний дараа, -ж дуусгаад&quot; гэсэн утгатай дүйнэ. -고 тай харьцуулахад өмнөх үйлдэл нь бүрмөсөн үйлдэгдэж дууссаныг илэрхийлнэ.</p>\r\n\r\n<p>국을 끓이고 나서 밥을 지으세요.</p>\r\n\r\n<p>쇠고리를 볶고 나서 채소를 넣으세용.</p>\r\n\r\n<p>설명을 다 듣고 나서 질문하세요.</p>\r\n', 0),
(121, '(으)로 - аар4', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгийн ард залгаж, арга хэрэгслийг илэрхийлдэг тийн ялгалын нөхцөл. Монгол хэлний үйлдэхийн тийн ялгалын &quot;-аар4&quot; нөхцөлтэй утга ижил. ㄹ гийгүүлэгч болон эгшгээр төгссөн нэр үгийн ард 로, ㄹ -ээс бусад гийгүүлэгчээр төгссөн нэр үгийн ард 로 -г тус тус залгана.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">삼계탕은 닭과 인삼으로 만듭니다.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">숟가락과 젓가락으로 음식을 먹습니다.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">닭 가슴살로 만든 음식은 칼로리가&nbsp; 낮아 다이어트에 좋습니다.</span></p>\r\n', 0);
INSERT INTO `rules` (`r_id`, `rule`, `content`, `level_id`) VALUES
(122, '-다가 - ж байгаад', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, хийж байсан ямар нэгэн үйлдлээ зогсоож өөр үйлдлээр сольж хийх санааг илэрхийлэх бөгөөд монгол хэлнээ &quot;-ж байгаад&quot; гэсэн утгаар хэрэглэгдэнэ. Энэ тохиолдолд өмнөх болон дараах өгүүлбэрийн эзэн бие нэг байна.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">쇠고기를 볶다가 야채를 넣으세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">된장찌개를 끓이다가 두브를 넣으세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">학교에 가다가 친구를 만났어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 손을 왜 다쳤어요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 감자를 볶다가 기름이 튀어서 데었어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 지원 씨를 어디에서 봤어요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B:과 사무실에 가다가 봤어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><strong>Тодруулга:</strong>&nbsp;Зарим тэмдэг үйл үгийн үндсэнд залгаж, тухайн нөхцөл байдал үргэлжлэхээ больж өөр нөхцөл байдлаар солигдоход хэрэглэнэ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">예) 날씨가 맑다가 잡자기 흐려졌어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">밖이 조용하다가 점점 시끄러워졌어요.</span></p>\r\n', 0),
(123, '-게', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тэмдэг үйл үг болон зарим үйл үгийн үндсэнд залгаж, тухайн үйлдэл, нөхцөл байдлын арга барил хэр хэмжээг илэрхийлдэг нөхцөл. Энэ тохиолдолд тэмдэг үйл үг болон үйл үг нь өгүүлбэрт дайвар үгийн үүрэг гүйцэтгэж, яаж? гэсэн асуултанд хариулагддаг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">맛있게 드세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">한국 사람들은 맵게 먹어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">글씨를 예쁘게 쓰세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 커피를 어떻게 탈까요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 저는 좀 연하게 타 주세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 제 말을 이해하셨어요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 아니요. 잘 모르겠어요. 좀 더 쉽게 설명해 주세요.</span></p>\r\n', 0),
(124, '-기 쉽게 - ж нь амархан', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, ямар нэгэн үйлдэл хийх болон ямар нэгэн нөхцөл байдал бий болох боломж өндөг байгааг илэрхийлж монгол хэлнээ. &quot;-ж нь амархан&quot; гэсэн утгатай дүйцнэ. -기 -ийн ард 가 нөхцөлийг залгаж хэрэглэх нь ч бий.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">비밀번호는 메모하지 않으면 잊어버리기 쉬워요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">단 음식을 많이 먹으면 충치가 생기가 쉬워요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">옷을 얇게 입고 다니면 감기에 걸리가 쉬워요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 신용카드를 만들면 과소비하기 쉽겠지요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 맞아요. 신용카드는 안 쓰는 게 좋아요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 그렇게 잠을 못 자면 몸살 나기 쉬워요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 그래서 좀 일찍 집에 가려고 해요.</span></p>\r\n', 0),
(125, '-는 동안 - х хооронд, х хугацаанд', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндэс болон 있다, 없다 үгийн үндсэнд залгаж, ямар нэгэн үйлдэл болон нөхцөл байдлын үргэлжилж байгаа цаг хугацааг илэрхийлнэ. Монгол хэлний &quot;-х хооронд, -х хугацаанд&quot; зэрэг хэллэгтэй утга дүйнэ. -는 동안 -ийн ард 에 нөхцөлийг залгаж хэрэглэж болно.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">은행에서 번호표를 뽑고 기다리는 동안 커피 한 잔 드세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">어머니께서 주무시는 동안 제가 설거지를 했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">여기 있는 동안 저는 아주 편하게 생활했습니다.</span></p>\r\n', 0),
(126, '-(으)려면 - х гэж байгаа бол', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, ямар нэгэн үйлдэл хийх хүсэл эрмэлзэлтэй байгаа эсэхийг таамаглахад хэрэглэх бөгөөд монгол хэлнээ &quot;-х гэж байгаа бол&quot; гэсэн хэллэгтэй утга дүйнэ. Үйл үгийн үндэс эгшиг болон ㄹ гийгүүлэгчээр төгсвөл 려면, ㄹ -ээс бусад гийгүүлэгчээр төгсвөл 으려면 -г тус тус залгана.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">송금을 하려면 계좌 본호를 일아야 해요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">현금카드로 돈을 찾으려면 비밀번호를 알아야 해요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">주말에 쉬려면 평일에 부지런히 일하세요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">A: 통장을 만들고 싶은데 뭐가 필요해요?&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">B: 통장을 만들려면 도장과 신분중이 필요해요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">A: 사장님을 만나고 싶은데요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">B: 사장님을 만나시려면 오후에 다시 오세요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Тодруулга:</strong>&nbsp;Үйл үгийн үндсэнд залгаж, өөрийн хүсэл эрмэлзэлээс хамаарахгүй нөхцөл байдалд бий болох үйлдлийг таамаглах бтохиолдолд бас хэрэглэдэг.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">예) 그 책이 나오려면 한 달쯤 더 걸릴 거예요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">컴퓨터가 켜지려면 시간이 좀 걸리니까 기다리세요.</span></p>\r\n', 0),
(127, '처럼 - шиг, мэт', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэг үгийн ард залгаж, харьцуулах, зэргэцүүлэх утга илтгэдэг нөхцөл. Монгол хэлнээ &quot;шиг, мэт&quot; зэрэг адилтгалын утгаар орж өмнө нэр үгтэйгээ ойролцоо, адил болохыг илтгэнэ. 같이 (хамт) үгээр сольж хэрэглэж болно.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지훈 씨는 운동선수처럼 운동을 잘해요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">민준 씨 딸도 아빠를 닮아서 밝고 사교적이에요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">주인 아주머니가 어머니처럼 잘해 주세요.</span></p>\r\n', 0),
(128, '-군요 - ... юм байна ш дээ', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж, ямар нэгэн зүйлийг шинээр мэдсэн, мэдэрсэнээ илэрхийлэх бөгөөд монгол хэлнээ &quot;... юм байна ш дээ&quot; утгаар хэрэглэгддэг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тэмдэг үйл үгийн үндсэнд -군요, үйл үгийн үндсэнд -는군요-г залгадаг. Өнгөрсөн цагт болж өнгөсрөн үйл явдлыг тухай ярихад -았/었군요, тааварлах болон таамаглаж ярихад 겠군요 -г тус тус залгана.</span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"height:40px; width:400px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center;\">&nbsp;</td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">현재(진행, 습관)</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">과거(경험,완료)</span></td>\r\n			<td colspan=\"2\" rowspan=\"1\" style=\"text-align: center;\"><span style=\"font-size:14px\">미래 (의지, 추측)</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">동사</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">-는군요</span></td>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align: center;\"><span style=\"font-size:14px\">-았/었군요</span></td>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-ㄹ 거군요</span></p>\r\n\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-을 거군요</span></p>\r\n			</td>\r\n			<td colspan=\"1\" rowspan=\"3\" style=\"text-align: center;\"><span style=\"font-size:14px\">-겠군요</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">형용사</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">-군요</span></td>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align: center;\"><span style=\"font-size:14px\">-</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">이다</span></td>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">군요</span></p>\r\n\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">이군요</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">였군요</span></p>\r\n\r\n			<p style=\"text-align: center;\">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\">몽골에는 한국어를 공부하는 학생이 참 많군요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">지훈 씨는 정말 잘 먹는군요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">서영 씨, 요즘 일이 많아서 피곤하겠군요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Тодруулга:</strong>&nbsp;Найз нөхөд болон өөрөөсөө албан тушаал, нас бага хүнд, мөн өөрө өөртөө хэлэхэд 구나 -г хэрэглэдэг.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">예) 민재는 정말 잘 먹는구나.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내가 공부를 많이 했구나.</span></p>\r\n', 0),
(129, '-(으)ㄴ/는 편이다 - ... талтай', '<p><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж, ерөнхийдөө ямар нэгэн төрөл хэсэгт хамаарахыг илэрхийлнэ. Монгол хэлнээ &quot;... талтай.&quot; гэсэн хэлбрэтэй дүйнэ. Эгшгээр болон ㄹ дэвсгэр үсгээр төгссөн тэмдэг үйл үгийн үндсэнд, мөн 이다, 이니다 туслах үйл үгийн ард -ㄴ 편이 다, үйл үг болон 있다, 없다 үгийн ард -는 편이다 -г тус тус залгана.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">제 동생은 조금 소극적인 편이에요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">그 사람은 말이 많은 편이에요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">저는 사람을 쉽게 사귀는 편이에요.</span></p>\r\n', 0),
(130, '-는 바람에 - \"... уршгаар, ... болохоор, -аад4\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, үүссэн нөхцөл байдлын учир шалтгааныг илэрхийлнэ. Монгол хэлнээ &quot;... уршгаар, ... болохоор, -ээд&quot; зэрэг учир шалтгааныг илэрхийлсэн холбох нөхцөлтэй утга дүйнэ. Санаанд оромгүй нөхцөл байдал үүсч, тэр нь дараагийн үйлдэлд сөрөг нөлөө үзүүлэхэд хэрэглэнэ. Арын өгүүлбэр нь захиран тушаах, уриалан хүсэх хэлбэрт бичигдэхгүй.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지갑을 놓고 오는 바람에 다시 학교로 가는 길이에요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">갑자기 컴퓨터가 고장 나는 바람에 이메일을 확인하지 못했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">비가 오는 바람에 옷이 다 젖었어요.</span></p>\r\n', 0),
(131, '-는 중이다 - \"... ж байна\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж, тухайн үйлдэл үйлдэгдсээр байгааг илэрхийлнэ. Монгол хэлнээ &quot;... ж байна&quot; утгатай дүйнэ. N 하다 хэлбэртэй үйл үгийн хувьд N 중이다 хэлбэртэй бичдэг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지감을 놓고 와서 집으로 돌아가는 중이에요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">시험 보는 중에 전화가 왔어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">회의 중에 커피를 쏟았어요.</span></p>\r\n', 0),
(132, '-도록 하다 - уулах, үүлэх', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгэнд залгаж, бусдаар ямар нэгэн үйлдэл үйлдүүлэх үед хэрэглэх бөгөөд монгол хэлнээр бусдаар үйлдүүлэхийн &quot;-уулах, үүлэх&quot; нөхцөлтэй утга дүйнэ. Захиран тушаах хэлбэрт хэрэглэгдвэл ямар нэгэн үйлдэл хийхийг шаардах, захирах утга илтгэнэ. -도록 하겠다 хэлбэрээр орох тохиолдолд өгүүлэгчийг ямар нэгэн үйлдэл хийх гэсэн хүсэл эрмэлзэл, санааг илэрхийлнэ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">늦게 온 사람에게 청소를 하도록 했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">휴지를 떨어뜨린 사람에게 그것을 줍도록 했이요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">어제는 약속을 꼭 지키도록 하세요.</span></p>\r\n', 0),
(133, '-(으)ㄴ/는/(으)ㄹ 줄 알다 - \"... юм байх гэж бодох\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг болон тэмдэг үйл үгийн үндсэнд залгаж, тухайн зүйл өгүүлэгчийн бодож байсанаас өөр байх тохиолдолд хэрэглэнэ. Үйл үгийн өнгөрсөн цагт -(으)ㄴ 줄 알다, одоо цагт -는 줄 알다, ирээдүй цагт -(으)ㄹ 줄 알다 хэлбэртэй болдог. Тэмдэг үйл үгэнд голдуу -(으)ㄴ 굴 알다 -г залгах бөгөөд таамаглах утгаар орох тохиолдолд -(으)ㄹ 줄 알다 -г хэрэглэдэг. -이다 хэлбэртэй туслах үйл үгэнд одоо цагт болон дадал зуршлыг илэрхийлэхэд 인 줄 알다, таамаглах утгаар орох тохиолдолд 일 줄 알다 -г тус тус хэрэглэнэ. Монгол хэлнээ &quot;... юм байх гэж бодох&quot; гэсэн хэллэгтэй утга дүйнэ.</span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:1000px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center;\">&nbsp;</td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">현재(진행, 습관)</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">과거(경험, 완료)</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">미래(의지, 추즉)</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">동사</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">-는 줄 알다</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">-(으)ㄴ 줄 알다</span></td>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align: center;\"><span style=\"font-size:14px\">-(으)ㄹ 줄 알다</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">형용사</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">-(으)ㄴ 줄 알다</span></td>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align: center;\"><span style=\"font-size:14px\">-</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">이다</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">인 줄 알다</span></td>\r\n			<td style=\"text-align: center;\"><span style=\"font-size:14px\">일 줄 알다</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">수해 씨가 약속을 잊어버린 줄 알고 화를 냈어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">토야 씨가 체첵 씨보다 큰 줄 알았어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">저보다 나이가 어린 줄 알고 반말을 했는데 저보다 나이가 많았어요.</span></p>\r\n', 0),
(134, '-(으)ㄹ 줄 알다/모르다 - \"-ж чадна/чадахгүй\", \"-ж мэднэ/мэдэхгүй\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн ард залган тухайн үйлийг гүйцэтгэх арга чадалтай эсэхийг илтгэх утгатай. Монгол хэлний &quot;-ж чадна/чадахгүй&quot;, &quot;-ж мэднэ/мэдэхгүй&quot; гэсэн хэллэгтэй дүйнэ. Үйлийн үндэс эгшгээр төгсвөл -ㄹ 줄 알다/모르다 хэлбэртэй харин гийгүүлэгчээр төгсвөл -을 줄 알다/모르다 хэлбэрийг залгадаг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">저는 한국어로 계약서를 쓸 줄 몰라요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">설렁거 씨는 피아노를 칠 줄 알아요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">한국에 오래 살았지만 한국 노래를 부를 줄 몰라요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><strong>Тодруулга:</strong>&nbsp;Дээрх хэлбэр нь чадвар, боломжийн утга илрүүлдэг -(으)ㄹ 수 있다/없다 хэлбэрээс ялгаатай байдаг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">예) 지금 바로 떠날 수 있ㅇ어요 (O)</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지금 바로 떠날 줄 알아요 (X)</span></p>\r\n', 0),
(135, '에 비해서 - \"-тай харьцуулахад\", \"-г бодвол\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгийн ард залган нэр ба үйлийг харьцуулах утга илтгэнэ. Өөрөөр хэлбэл, өмнөх тусагдахуунтай харьцуулахад үйлийн үр дүн өөр байгаа гэсэн утга илтгэдэг. Монгол хэлний &quot;-тай харьцуулахад&quot;, &quot;-г бодвол&quot; гэх мэт хэллэгтэй дүйнэ. Заримдаа төгсгөлд нь орсон -서 -г хурааж болно.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">요즘은 전세에 비해서 월세가 더 많아요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">가격에 비해서 품질이 좋은 것 같아요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">바야르 씨 성격에 비해 체첵 씨 성격이 더 밝아요.</span></p>\r\n', 0),
(136, '-기 바라다 - \"-г хүсэн ерөөе\", \"-г хүсье\", \"... на уу\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, зарим тэмдэг үйл үгийн ард залган хүсэн ерөөх утга илтгэнэ. Тухайн үйлийн бүтэмжийг ерөөж, санал хүсэлтээ албан болон бичгээр илэрхийлэхэд ордог хэлбэр юм. Монгол хэлний &quot;-г хүсэн ерөөе&quot;, &quot;-г хүсье&quot;, &quot;... на уу&quot; гэх мэт хэллэгтэй дүйж болно.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">항상 건강하시기(를) 바랍니다.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">도서관에서는 조용히 해 주시기 바랍니다.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">포장 이사를 하시려면 이삿짐센터로 문의해 주시기 바랍니다.</span></p>\r\n', 0),
(137, '아무 (이)나 - \"хэн ч, юу ч байсан хамаагүй -ж болно\"', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Нэр үгийн ард залган тухайн үйлийн үр дүнгээс шалтгаалахгүйгээр &quot;хэн ч, юу ч байсан хамаагүй -ж болно&quot; хэмээх утга илтгэнэ. Монгол хэлний &quot;-ч байсан&quot;, &quot;-ч хамаагүй&quot; хэмээх утгатай дүйнэ. Хүнийг төлөөлсөн тохиолдолд 아무나 хэлбэрийг ашиглана.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">옛날에는 아무 때나 이사 가지 않고 좋은 날을 선택해서 아사했어요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">배고플 때 냉장고에서 아무 음식이나 꺼내서 드세요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">스 회의는 중요하기 때문에 아무나 들어갈 수 없어요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>Тодруулга:</strong>&nbsp;Гэхдээ ард нь үгүйсгэх нөхцөл олгодог 아무도 хэлбэрээс ялгаатай гэдгийг санаарай.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">예) 아무도 오지 마세요. (O) 아무도 오세요 (X)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">아무나 오면 안돼요. (O) 아무나 오세요.&nbsp;(X)</span></p>\r\n', 0),
(138, '-다고 하다 - \"... гэдэг/гэсэн/гэнэ\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн ард залган бусад хүнээс сонссон зүйлээ дам байдлаар ишлэн өгүүлэх утга илтгэнэ. Монгол хэлний &quot;... гэдэг/гэсэн/гэнэ&quot; гэх мэт ишлэл өгүүлбэртэй дүйнэ. Эгшиг бол ㄹ дэвсгэр үсгээр төгссөн бол -ㄴ다고 하다 хэлбэртэй, гийгүүлэгчээр төгсвөл -는다고 하다 хэлбэр, 있다, 없다 бэ тэмдэг үйл үгийн ард -다고 하다 хэлбэр, 이다, 아니다 -н ард -(이)라고 하다 хэлбэр тус тус залгадаг.</span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center; height: 40px;\">&nbsp;</td>\r\n			<td style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">현재(진행, 습관)</span></td>\r\n			<td style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">과거 (경험, 완료)</span></td>\r\n			<td style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">미래(의지, 추측)</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">동사</span></td>\r\n			<td style=\"height: 40px;\">\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-ㄴ 다고 하다</span></p>\r\n\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-는 다고 하다</span></p>\r\n			</td>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">-았/었다고 하다</span></td>\r\n			<td colspan=\"1\" rowspan=\"3\" style=\"height: 40px;\">\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-(으)ㄹ 거라고 하다</span></p>\r\n\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">-겠다고 하다</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">형용사</span></td>\r\n			<td style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">-다고 하다</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: center; height: 40px;\"><span style=\"font-size:14px\">이다</span></td>\r\n			<td style=\"height: 40px;\">\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">라고 하다</span></p>\r\n\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">이라고 하다</span></p>\r\n			</td>\r\n			<td style=\"height: 40px;\">\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">였다로 하다</span></p>\r\n\r\n			<p style=\"text-align: center;\"><span style=\"font-size:14px\">이었다고 하다</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">체첵 씨는 주말마다 야외로 드라아브를 간다고 해요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지원 씨가 저한테 이번 주에는 시간이 별로 없다고 했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">민준 씨가 우리한테 제주도가 아주 유명한 관광지라고 했어요.</span></p>\r\n', 0),
(139, '-냐고 하다 - \"-сан уу гэж бай-\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн ард залган бусдын асуусан асуултыг дам байдлаар ишлэн өгүүлэх утга илтгэнэ. Монгол хэлний &quot;-сан уу гэж бай-&quot; ишлэл асуух өгүүлбэртэй дүйж болно. 이다 -н ард -(으)냐고 하다 хэлбэр ордог.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">서영 씨가 저한테 비행기 표를 예약했냐고 해요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">민정 씨가 요즘 몽골 날씨가 어떠냐고 해요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">너한테 직업이 뭐냐고 해서 학생이라고 대답했어요.</span></p>\r\n', 0),
(140, '-(으)ㄹ까 하다 - \"-даг юм билүү гэж бай-\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн ард залган баттай шийдвэрт хүрээгүй ч тухайн үйлийг хийх санаатай байгаа хэмээх утга илтгэнэ. Монгол хэлний &quot;-даг юм билүү гэж бай-&quot; гэсэн хэлбэртэй дүйнэ. Эгшиг болон ㄹ дэвсгэр гийгүүлэгчээр төгссөн бол -ㄹ까 하다 хэлбэр, гийгүүлэгчээр төгсвөл -을까 하다 хэлбэр залгадаг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">이번 방학에는 한국 여행을 할까 해요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">방학 때 전공책들을 좀 읽을까 해요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">학기 끝날 때쯤 한국 친구에게 연락해 볼까 해요.</span></p>\r\n', 0),
(141, '반말 Хүндэтгэлийн бус хэлбэр', '<p><span style=\"font-size:14px\">Өгүүлбэрийн төгсгөх нөхцөлд ордог энэ хэлбэрийг нас бага хүнд, эсвэл найз нөхдийн дотно хүрээнд хэрэглэдэг. Монгол хэлний хүндэтгэлийн бус хэлбэр ихэвчлэн хоёрдугаар биеийн захиран тушаах 0 хэлбэр (яв, ир, гүй гэх мэт) болон гуйн шаардах &quot;-аач&quot; хэлбэрээр илэрдэг уир эдгээртэй дүйлгэж болно. Солонгос хэлний хүндэтгэлийн бус хэлбэрийг албан ба энгийн найруулга гэж ангилах бөгөөд албан найруулгын хүндэтгэлийн бус хэлбэр нь олон нөхцөлтэй байдаг бол энгийн найруулгын хүндэтгэлийн бус хэлбэрийг үйл үг, тэмдэг үйл үгийн үндсэнд -아/-어, нэр үгэнд -(이)야 нөхцөл залгаж илрүүлнэ. Доор хүснэгтийг үзнэ үү.</span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td><span style=\"font-size:14px\">평서형</span></td>\r\n			<td><span style=\"font-size:14px\">의문형</span></td>\r\n			<td><span style=\"font-size:14px\">청유형</span></td>\r\n			<td><span style=\"font-size:14px\">명령형</span></td>\r\n			<td><span style=\"font-size:14px\">감탄형</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"3\"><span style=\"font-size:14px\">격식체</span></td>\r\n			<td><span style=\"font-size:14px\">동사</span></td>\r\n			<td><span style=\"font-size:14px\">-ㄴ/는다</span></td>\r\n			<td><span style=\"font-size:14px\">-나/냐?</span></td>\r\n			<td><span style=\"font-size:14px\">-자</span></td>\r\n			<td><span style=\"font-size:14px\">-아/어라</span></td>\r\n			<td><span style=\"font-size:14px\">-는구나</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\">형용사</span></td>\r\n			<td><span style=\"font-size:14px\">-다</span></td>\r\n			<td><span style=\"font-size:14px\">-니/냐?</span></td>\r\n			<td style=\"width:40px\"><span style=\"font-size:14px\">-</span></td>\r\n			<td><span style=\"font-size:14px\">-</span></td>\r\n			<td><span style=\"font-size:14px\">-누나</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\">이다</span></td>\r\n			<td><span style=\"font-size:14px\">(이)다</span></td>\r\n			<td><span style=\"font-size:14px\">(이)니/냐?</span></td>\r\n			<td><span style=\"font-size:14px\">-</span></td>\r\n			<td><span style=\"font-size:14px\">-</span></td>\r\n			<td><span style=\"font-size:14px\">(이)구나</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:14px\">요즘 고민이 많아서 잠이 안 와.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">학교 앞에 새로 생긴 그 커피숍 정말 예뻐.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내 친구는 운동하는 것을 좋아한다.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">내일 뭐 할 거야?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">한국말을 정말 잘 하는구나!</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Тодруулга:</strong>&nbsp;Энгийн найруулгын -아/어요 нөхцөлөөс төгсгөлийн -요 -г гээвэл энгийн найруулгын хүндэтгэлийн бус хэлбэр үүснэ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">예) 요즘 시험 때문에 스트레스가 많아요.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">-&gt; 요즘 시험 때문에 스트레스가 많아.</span></p>\r\n', 0),
(142, '아/야 - \"... аа4\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгийн ард залган найх нөхөд, нас залуу хүнтэй харьцахад, мөн хүн, амьтныг дуудах атгаар хэрэглэнэ. Монгол хэлний дуудахын &quot;... аа4&quot; нөхцөлтэй дүйж болно. Эгшгээр төгссөн үгэнд -야, гийгүүлэгчээр төгссөн үгэнд -아 хэлбэр залгана.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">수진아, 무슨 고민 있어?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">설렁거, 이따 5시에 영화 보러 가자.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">수진아, 이리 와.</span></p>\r\n', 0),
(143, '-다(가) 보면 - \"-аад байх юм бол, -аад байвал\"', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">Үйл үгийн ард залган тухайн нэг үйл өрнөж байх зуур, эсвэл тухайн үйлийг үргэлжлүүлж хийсэн нөхцөлд байдал өөрчлөгдөнө хэмээх утга илтгэнэ. Монгол хэлний &quot;-аад байх юм бол, -аад байвал&quot; гэх хэллэгтэй дүйнэ. -다가 нөхцөлийг -다 -аар хураангуйлан бичиж болно.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">친구들과 이야기하다가 보면 스트레스가 풀려요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">열심히 공부하다 보면 한국어 실력이 늘게 될 거예요.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">계속 가다 보면 왼쪽에 서점이 보일 거예요.</span></p>\r\n', 0),
(144, '-(으)ㅁ', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж аливаа баримт мэдээг товч тэмдэглэх, эсвэл зарлалын чанартай мэдээг төгсгөх үед хэрэглэнэ. Ихэвчлэн албан бичиг, тэмдэглэл зэрэгт орж өгүүлбэрийг төгсгөдөг. Монгол хэлнээ тохирох нөхцөл үгүй. Эгшиг болон ㄹ гийгүүлэгчээр төгссөн үйл үг, тэмдэг үйл үгийн ард -ㅁ, харин ㄹ гийгүүлэгчээс бусад гйигүүлэгчээр төгссөн үйл үгийн ард -음 хэлбэр залгана.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">파일을 이메일로 보냈음.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">한국학과 홈페이지에 수업 정보를 게시했음.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">월요일까지 출장 중임.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지원 씨에게서 오전 10시쯤 전화가 왔음.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">올란바토르에는 밤늦게 도착했음.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">오후부터 구름이 끼고 곳에 따라 비가 오겠음.</span></p>\r\n', 0),
(145, '-(으)라고 하다 - \"-аарай гэсэн\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залган бусдын захиран шаардсан хүсэлтийг уламлах утга илтгэх дам ишлэл өгүүлбэрийн төгсгөх нөхцөл юм. Монгол хэлний &quot;-аарай гэсэн&quot; гэсэн ишлэл өгүүлбэрийн утгатай дүйнэ. Эгшиг болон ㄹ гийгүүлэгчээр төгссөн үйл үгийн ард -라고 하다, харин ㄹ гийгүүлэгчээс бусад гийгүүлэгчээр төгссөн үйл үгийн ард -(으)라고 하다 хэлбэр ордог.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">주다 (өгөх) үйл үгийн хувьд, хэн нэгэн хүнд ямар нэгэн зүйл хийж өгөх талаар дам ярихдаа 주라고 하다 (-д ... -ж өгөөрэй гэж) хэлбэр, харин хэн нэгэн хүн өөрөө ямар нэгэн зүйлийг хийж өгөхийг гуйж хүсэх үед 달라고 하다 (-ж өгөөрэй гэж) гэсэн хэлбэрийг ашиглана. Үүнээс гадна -지 마세요 (битгий -ээрэй) гэдэг хориглох утгыг дам байдлаар хэлэхдээ -지 말라고 하다 (битгий -ээрэй гэсэн) хэлбэрээр хувилгана.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">자료는 바탕 화면에 저장해 두라고 하세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">자료는 인터넷에서 찾으라고 하셨어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">한지원 씨가 도와 달라고 했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">한지원 씨가 정우 씨를 도와주라고 했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">정우 씨에거 몽임에 늦지 말라고 전해 주세요.</span></p>\r\n', 0),
(146, '-자고 하다 - \"-я гэсэн\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залган бусдын гуйлт хүсэлтийг дам байдлаар дамжуулах утга илтгэнэ. Монгол хэлний &quot;-я гэсэн&quot; хэмээх утгатай дүйнэ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지원 씨가 메신저로 연락하자고 했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">정우 씨가 시험 자료를 카페에 올리자고 했어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">선생님께서 사무실에서 만나자고 했어요.</span></p>\r\n', 0),
(147, '-아/어 가다 - \"-ж байна\", \"-ж байгаарай\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн ард залган тухайн үйл байдлын өөрчлөлт үргэлжлэл явагдаж буй утга илтгэнэ. Монгол хэлний &quot;-ж байна&quot;, &quot;-ж байгаарай&quot; гэх мэт хэлбэртэй дүйж болно. Мөн -아/어 가며 (-ж байгаа бөгөөд), -아/어 가면서 (-нгаа) зэрэг хэлбэрээр орж эхний үйлдэлтэй зэрэгцэн дараагийн үйл үргэлжлэн явагдах утга илтгэх нь бий.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">어제 숙제가 다 끝나 가요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">밥 더 먹어 가니까 조금만 기다려 주세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">좀 쉬어 가면서 일합시다.</span></p>\r\n', 0),
(148, '-아/어 오다 - \"-ж ирэх\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн залган тухайн үйл байдлын өөрчлөлт үргэлжлэн явагдаж ирсэн хэмээх утга илтгэнэ. Монгол хэлний &quot;-ж ирэх&quot; гэх хэлбэртэй дүйж болно.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">취업 준비 때문에 컴퓨터와 외국어를 계속 공부해 왔어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">앞으로의 진로에 대해 오랜 시간 고민을 해 왔어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지금까지 일해 온 직장을 떠나게 됐어요.</span></p>\r\n', 0),
(149, '-아/어야겠다 - \"-гүй бол болохгүй нь\", \"-дэг юм шүү, -дэг хэрэг\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн ард залган тухайн үйлдлийг заавал хийх ёстой юм уу, хийхгүй бол болохоо болилоо&nbsp;гэж сэтгэл шулуудаж буй байдлыг илэрхийлэх утгатай. Монгол хэлний &quot;-гүй бол болохгүй нь&quot;, &quot;-дэг юм шүү, -дэг хэрэг&quot; хэмээх хэлбэртэй дүйнэ. Эгшгийн ард -여야, гийгүүлэгчийн ард -이어야 нөхцөл залгадаг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">이번 도전에서는 꼭 성공해야겠어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">다음 학기에는 학점을 잘 받아야겠어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">한국 회상에서 일하려면 한국어를 더 갈해야겠어요.</span></p>\r\n', 0),
(150, '-았/었으면 좋겠다 - \"-вэл сайнсан\", \"-вэл зүгээр сан\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн ард залган өнгөрсөн ба өнөөгийн байдалтай эсрэгцэх байдлыг урьчилан таамаглах, эсвэл биелээгүй зүйлийг тийм ийм байгаасай хэмээн хүсэмжлэх утга илтгэнэ. Монгол хэлний &quot;-вэл сайнсан&quot;, &quot;-вэл зүгээр сан&quot; хэмээх хэллэгтэй дүйнэ. 좋다 (сайн) үйл үгийн оронд 하다 (хийх) юм уу 싶다 (хүсэх) зэрэг үйл үг орж болно.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">졸업 후에 공무원이 됐으면 좋겠어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">방학 때 한국 회사에서 인턴사원으로 일했으면 좋겠어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">제가 좀 더 머리가 좋았으면 좋겠어요.</span></p>\r\n', 0),
(151, '(아무리) -아/어도 - \"хичнээн ... байсан ч\", \"...ч гэсэн\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн ард залган өмнөх үйлийн нөхцөл байдлаас хамаарахгүйгээр дараагин үйл үйлдэгдэх утга илтгэнэ. Монгол хэлний &quot;хичнээн ... байсан ч&quot;, &quot;...ч гэсэн&quot; гэх утгатай дүйнэ. ㅏ, ㅗ эгшгийн ард 어도, үүнээс бусад эгшгийн ард -어도 хэлбэр залгадаг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">저는 아무리 바빠도 일주일에 란 번찍 한국 영확를 봐요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">아무리 배가 불러도 음식을 버리면 안 돼요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">아무리 힘들어도 포기하지 마세요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 내일 비가 오면 행사를 안 합니까?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B:&nbsp;아니요, 내일 비가 와도 행사를 합니다.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 어제 설렁거 씨하고 영화 갈 봤어요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 아니요, 아무리 기다려도 오지 않아서 그냥 혼자 봤어요.</span></p>\r\n', 0),
(152, '-거든(요) - \"... шүү дээ\", \"... байхгүй юу\", \"...л даа\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн ард залгаж, асуултын хариу болгох юм уу эсвэл өмнөх өгүүлбэрийн утгыг тодруулж тайлбарлах маягийн утга илтгэнэ. Монгол хэлний &quot;... шүү дээ&quot;, &quot;... байхгүй юу&quot;,&nbsp;&quot;...л даа&quot; гэх мэт хэлбэртэй дүйнэ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">이 드라마 한번 보세요. 남자 주인공이 정말 멋있거든요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">그 남자 배우가 요즘 정말 인기가 있어요. 정말 잘생겼거든요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">내가 오늘 한턱낼게. 장학금을 받았거든.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><strong>Тодруулга:</strong>&nbsp;Өгүүлбэрийн агуулгыг тайлбарлах маягаар өгүүлсний дараах хэлэх гэсэн санаагаа үргэлжлүүлэх тохиолдолд эл нөхцөлийг мөн ашигладаг.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">예) 영화 표가 두 장 생겼거든요. 같이 가실래요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">이번 시험이 아주 어려웠거든요. 그래서 잘 못 봤어요.</span></p>\r\n', 0),
(153, '-던데요', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Тэмдэг үйл үг, үйл үгийн ард залган өнгөрсөн явдлыг дурсан санахын сацуу тэр үед төрсөн сэтгэгдлээ эргэн дурсах утга илтгэнэ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">그 영화 보셨어요? 지난 주말에 봤는데 정말 재미있던데요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">어제 집에 가다가 설렁거 씨 님자 친구를 봤어요. 키가 정말 크던데요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">방학 때 제주도에 갔어요. 경치가 정말 아름답던데요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 저 배우 정말 멋있지요?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 네, 연기도 정말 잘하던에요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A: 바야르 씨는 책을 정말 좋아하는 것 같아요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B: 네, 책을 정말 많이 읽던데요.</span></p>\r\n', 0),
(154, '대로 - \"-аар хийх\", \"... ёсоор/дагуу хийх\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Нэр үгийн ард залган өмнөх үгэнд үндэслэн хийх, эсвэл өмнөх утгаас гажилтгүй тэр хэвээр нь хийх гэсэн санаа илтгэнэ. Монгол хэлний &quot;-аар хийх&quot;,&nbsp;&quot;... ёсоор/дагуу хийх&quot; гэсэн утгатай дүйнэ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">순서대로 해 드리겠습니다.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">약속대로 영화를 보여 줄게요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">친구에게 사실대로 말했습니다.</span></p>\r\n', 0),
(155, '-지 않으면 안 되다 - \"-гүй бол болохгүй\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үг, тэмдэг үйл үгийн үндсэнд залгаж тухайн үйлийг заавал гүйцэтгэх ёстой. эсвэл эл нөхцөл байдал дагуу үйлийг хүчтэй илэрхийлэхийн тулд үйлийн үгүйсгэх хэлбэрийг давхар хэрэглэсэн гэсэн үг. Монгол хэлний &quot;-гүй бол болохгүй&quot; гэсэн хэллэгтэй дүйж болно. Мөн үүнтэй ойролцоо -아야/어야 하다 гэдэг хэлбэр байдгийг санаарай.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">순서 표를 뽑고 기다리지 않으면 안 돼요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">허락을 받지 않으면 안 됩니다.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">공연에 방해가 되니까 휴대전화를 끄지 않으면 안 돼요.</span></p>\r\n', 0),
(156, '-(으)려던 참이다 - \"-ж байсан юм\"', '<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Үйл үгийн үндсэнд залгаж үйлийн эзэн бие тухайн цаг мөчид уг үйлийг үйлдэх гэж байсан болон үйлдэхийг завдаж байсан утга илтгэнэ. Монгол хэлний &quot;-ж байсан юм&quot; гэсэн хэллэгтэй дүйж болно. Эгшиг ба ㄹ гийгүүлэгчээр төгссөн үйл үгийн үндсэнд -려던 참이다 хэлбэр, ㄹ -ээс бусад гийгүүлэгчээр төгссөн үндсэнд -으려던 참이다 хэлбэр залгадаг. Ихэвчлэн одоо болон өнгөрсөн цагтай хамт хэрэглэгдэх бөгөөд -(으)려던 참에 гэх хэлбэрээр ч ордог.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">지금 막 자려던 참이에요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">제가 전화하려던 참이었어요.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">내가 나가려던 참에 친구가 찾아왔어요.</span></p>\r\n', 0);
